css:
@main_color: [[main_color: #978152]];
@menu_width: [[menu_width:auto]];
@menu_text_color: [[menu_text_color:#131210]];
@menu_background_color: [[menu_background_color:#f8f5ee]];
@menu_border_color: [[menu_border_color:#aa9961]];
@menu_font_size: [[menu_font_size:15px]];

@sub_menu_font_size: [[sub_menu_font_size:15px]];
@sub_menu_width: [[sub_menu_width:160px]];

#menu_class# ul, #menu_class# li,
#menu_class# span, #menu_class# a { margin: 0; padding: 0; }

#menu_class# {
width: @menu_width;
	background: @menu_background_color;
	border-bottom: 1px solid @menu_border_color;
	box-shadow: inset 0 2px 4px rgba(151, 129, 82, 0.5);
	-moz-box-shadow: inset 0 2px 4px rgba(151, 129, 82, 0.5);
	-webkit-box-shadow: inset 0 2px 4px rgba(151, 129, 82, 0.5);
}

#menu_class#:after { content:''; display: block; clear: both; }

#menu_class# a { 
	display: inline-block;
	font-family: Calibri, Arial, sans-serif;
	color: @menu_text_color; 
text-decoration: none; 
	-webkit-transition: all 0.2s;
	-moz-transition: all 0.2s;
	-o-transition: all 0.2s;
	-ms-transition: all 0.2s;
	transition: all 0.2s;
font-size: @menu_font_size;
}
#menu_class# a:hover { background: lighten(@main_color,10%); }

#menu_class# ul { 
	list-style: none; 
	height: 34px;
	padding: 10px 0; 
}
#menu_class# > ul > li {
	float: left; 
	height: 34px;
	margin: 0 5px;
	position: relative;
}
#menu_class# > ul > li > a {
	background: @main_color;
	box-shadow: inset 1px 1px 3px rgba(19,18,16,0.2);
	-moz-box-shadow: inset 1px 1px 3px rgba(19,18,16,0.2);
	-webkit-box-shadow: inset 1px 1px 3px rgba(19,18,16,0.2);
	border-radius: 17px;
	-moz-border-radius: 17px;
	-webkit-border-radius: 17px;
	line-height: 24px;
	padding: 5px 10px;
}
	#menu_class# > ul > li.active a {
		background: #131210;
		color: #f8f5ee;
	}

#menu_class# .has-sub ul {
	background: @menu_background_color;
	border: 1px solid #aa9961;
	border-top: 0 none;
	height: 0; overflow: hidden;
	padding: 0; position: absolute;
	opacity: 0; top: 44px; left: 0;
	width: @sub_menu_width; 

	-webkit-transition: opacity 0.2s;
	-moz-transition: opacity 0.2s;
	-o-transition: opacity 0.2s;
	-ms-transition: opacity 0.2s;
	transition: opacity 0.2s;
}
#menu_class# .has-sub:after {
	content: '';
	display: block;
	padding: 0 10px;
	position: absolute;
	bottom: -10px; left: 0;
	height: 10px; width: 200%;
}

#menu_class# .has-sub:hover ul {
	border-radius: 0 0 5px 5px;
	-moz-border-radius: 0 0 5px 5px;
	-webkit-border-radius: 0 0 5px 5px;
	height: auto;
	opacity: 1;
	padding: 10px;
}

#menu_class# .has-sub li, 
#menu_class# .has-sub li a { padding: 0 5px; width: 100%; font-size: @sub_menu_font_size;}
#menu_class# .has-sub li { margin: 8px 0; }
#menu_class# .has-sub li a { 
	border-radius: 5px 0 0 5px;
	-moz-border-radius: 5px 0 0 5px;
	-webkit-border-radius: 5px 0 0 5px;
	line-height: 120%; 
}
