css:
@import url(http://fonts.googleapis.com/css?family=Open+Sans:600);



/* Menu CSS */ 

#menu_class#, #menu_class# > ul{
	
	background: url(#include_path#highlight-bg.png) repeat;
	padding-bottom: 3px;
	font-family: 'Open Sans', sans-serif;
	font-weight: 600;
	
	
}

#menu_class#:before, #menu_class#:after, #menu_class# > ul:before, #menu_class# > ul:after {

	content: "";
	display: table;
	
}


#menu_class#:after, #menu_class# > ul:after {

	clear: both;
	
}

#menu_class# {
        width: [[menu_width:auto]];
	zoom:1;
	
}

#menu_class# > ul{
	
	background: url(#include_path#menu-bg.png) repeat;
	margin: 0;
	padding: 0;
	position: relative;
	
}

#menu_class# > ul li{
	
	margin: 0;
	padding: 0;
	list-style: none;
	
}

#menu_class# > ul > li{

	float: left;
	position: relative;
	
}

#menu_class# > ul > li > a{

	padding: 23px 26px;
	display: block;
	color: white;
	font-size: [[menu_font_size:13px]];
	text-decoration: none;
	text-transform: uppercase;
	text-shadow: 0 -1px 0 #9e3825;
	text-shadow: 0 -1px 0 rgba(116, 37, 2, .70);
	line-height: 18px;
	
}


#menu_class# > ul > li:hover > a{

	background: url(#include_path#hover.png) repeat;
	text-shadow: 0 -1px 0 #97321f;
	text-shadow: 0 -1px 0 rgba(122, 42, 26, .64);
	
}

#menu_class# > ul > li > a > span{

	line-height: 18px;
	
}

#menu_class# > ul > li.active > a, #menu_class# > ul > li > a:active{
	
	background: url(#include_path#active.png) repeat;
	
}



/* Childs */

#menu_class# > ul ul{

	opacity: 0;
	visibility: hidden;
	position: absolute;
	top: 120px;
	background: url(#include_path#highlight-bg.png) repeat;
	margin: 0;
	padding: 0;
	z-index: -1;

	
}

#menu_class# > ul li:hover ul{
	opacity: 1;
	visibility: visible;
	margin: 0;
	color: #000;
	z-index: 2;
	top:64px;
	left: 0;
	
}

#menu_class# > ul ul:before{

	content: "";
	position: absolute;
	top: -10px;
	width: 100%;
	height: 20px;
	background: transparent;
	
}


#menu_class# > ul ul li{

	list-style: none;
	padding: 0;
	margin: 0;
	width: 100%;
	
}

#menu_class# > ul ul li a{

	padding: 18px 26px;
	display: block;
	color: #393939;
	font-size: [[sub_menu_font_size:13px]];
	text-decoration: none;
	text-transform: uppercase;
	width: [[sub_menu_width:150px]];
	border-left: 4px solid transparent;
	-webkit-transition: all .35s ease-in-out;
	-moz-transition: all .35s ease-in-out;
	-ms-transition: all .35s ease-in-out;
	transition: all .35s ease-in-out;
	text-shadow: 0 1px 0 white;

}

#menu_class# > ul ul li a:hover{

	border-left: 4px solid #de553b;
	background: url(#include_path#hover.png) repeat;
	color: white;
	text-shadow: 0 1px 0 black;

}

#menu_class# > ul ul li a:active{

	background: url(#include_path#menu-bg.png) repeat;

}
