css:
@import url(http://fonts.googleapis.com/css?family=Source+Sans+Pro:500);

@color: [[main_color:#3b5998]];
@darker: darken(@color, 10%); /* 2f4b87 */
@darker2: darken(@color, 20%); /* 2f4b87 */
@lighter: lighten(@color, 10%); /* #1f325d */

/* Menu CSS */ 
#menu_class# {width: [[menu_width:auto]];}


#menu_class#, #menu_class# ul, #menu_class# ul li, #menu_class# ul li a{
	
	padding: 0;
	margin: 0;
	line-height: 1;
	font-family: 'Source Sans Pro', sans-serif;
	font-weight: 500;
	font-size: [[menu_font_size:16px]];
	color: [[menu_text_color:#ffffff]];
	-webkit-transition: all ease .3s;
	-o-transition: all ease .3s;
	-moz-transition: all ease .3s;
	-ms-transition: all ease .3s;
	transition: all ease .3s;
	
}

#menu_class#:before, #menu_class#:after, #menu_class# > ul:before, #menu_class# > ul:after {

	content: "";
	display: table;
	
}


#menu_class#:after, #menu_class# > ul:after {

	clear: both;
	
}

#menu_class# a{
	
	text-shadow: 0 1px 1px rgba(0, 0, 0, .2);
	
}

#menu_class# ul{
	
	background: @color;
	border-radius: 3px;
	border: 1px solid #2b4479;
	border: 1px solid @darker;
	-webkit-box-shadow: 0 1px 2px rgba(2, 2, 2, .25), inset 0 1px 1px rgba(255, 255, 255, .15);
	-o-box-shadow: 0 1px 2px rgba(2, 2, 2, .25), inset 0 1px 1px rgba(255, 255, 255, .15);
	-moz-box-shadow: 0 1px 2px rgba(2, 2, 2, .25), inset 0 1px 1px rgba(255, 255, 255, .15);
	-ms-box-shadow: 0 1px 2px rgba(2, 2, 2, .25), inset 0 1px 1px rgba(255, 255, 255, .15);
	box-shadow: 0 1px 2px rgba(2, 2, 2, .25), inset 0 1px 1px rgba(255, 255, 255, .15);
	
}

#menu_class# ul > li{
	
	float: left;
	list-style: none;
	
}

#menu_class# ul > li > a{
	
	display: block;
	text-decoration: none;
	padding: 15px 44px;
	position: relative;
	
}

#menu_class# ul > li > a:hover{
	
	background: @darker;

	-webkit-box-shadow: inset 0 0 1px @darker2;
	-o-box-shadow: inset 0 0 1px @darker2;
	-moz-box-shadow: inset 0 0 1px @darker2;
	-ms-box-shadow: inset 0 0 1px @darker2;
	box-shadow: inset 0 0 1px @darker2;

	-webkit-transition: all ease .3s;
	-o-transition: all ease .3s;
	-moz-transition: all ease .3s;
	-ms-transition: all ease .3s;
	transition: all ease .3s;
	
}

#menu_class# ul > li > a:hover:before{
	
	content: "";
	z-index: 2;
	position: absolute;
	border: 1px solid white;
	border-top: 0;
	border-bottom: 0;
	border-right: 0;
	width: 100%;
	height: 100%;
	top: 0;
	left: -1px;
	opacity: .2;

}

#menu_class# ul > li > a:hover:after{
	
	content: "";
	z-index: 2;
	position: absolute;
	border: 1px solid white;
	border-top: 0;
	border-bottom: 0;
	border-left: 0;
	width: 100%;
	height: 100%;
	top: 0;
	right: -1px;
	opacity: .2;

}

#menu_class# > ul > li > ul{
	
	opacity: 0;
	visibility: hidden;
	position: absolute;
	
}

#menu_class# > ul > li:hover > ul{
	
	opacity: 1;
	visibility: visible;
	position: absolute;
	border-radius: 0 0 3px 3px;
	-webkit-box-shadow: none;
	-o-box-shadow: none;
	-moz-box-shadow:none;
	-ms-box-shadow: none;
	box-shadow: none;
	
}

#menu_class# > ul > li > ul{
	
	width: [[sub_menu_width:200px]];
	position: absolute;
	
}

#menu_class# > ul > li > ul > li{
	
	float: none;
	position: relative;
	
}

#menu_class# > ul > li > ul > li > ul{
	
	opacity: 0;
	visibility: hidden;
	position: absolute;
	
}

#menu_class# > ul > li > ul > li:hover > ul{
	
	opacity: 1;
	visibility: visible;
	position: absolute;
	
}

#menu_class# > ul > li > ul > li > ul{
	
	left: [[sub_menu_width:200px]];
	top: 1px;
	width: [[sub_menu_width:200px]];
	
}

#menu_class# > ul > li > ul > li > ul > li{
	
	float: none;
	
}
