css:
@charset "UTF-8";

@menu_text_color: [[menu_text_color:#333333]];
@sub_menu_text_color: [[sub_menu_text_color:#333333]];
@sub_menu_text_hover_color: [[sub_menu_text_hover_color:#ae0001]];



#menu_class# {
  padding: 0;
  margin: 0;
  border: 0;
  width: [[menu_width:auto]];
}
#menu_class# ul,
#menu_class# ul li,
#menu_class# ul ul {
  list-style: none;
  margin: 0;
  padding: 0;
}
#menu_class# ul {
  position: relative;
  z-index: 597;
  float: left;
}
#menu_class# ul li {
  float: left;
  min-height: 1px;
  line-height: 1em;
  vertical-align: middle;
}
#menu_class# ul li.hover,
#menu_class# ul li:hover {
  position: relative;
  z-index: 599;
  cursor: default;
}
#menu_class# ul ul {
  visibility: hidden;
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 598;
  width: 100%;
}
#menu_class# ul ul li {
  float: none;
}
#menu_class# ul ul ul {
  top: 1px;
  left: 99%;
}
#menu_class# ul li:hover > ul {
  visibility: visible;
}
#menu_class# ul ul {
  top: 1px;
  left: 99%;
}
#menu_class# ul li {
  float: none;
}
#menu_class# ul ul {
  margin-top: 1px;
}
#menu_class# ul ul li {
  font-weight: normal;
}
/* Custom CSS Styles */

#menu_class#:after,
#menu_class# ul:after {
  content: '';
  display: block;
  clear: both;
}
#cssmenu > ul > li > a {  color: @menu_text_color;}
#cssmenu ul ul a {color: @sub_menu_text_color;}
#menu_class# a {
  display: inline-block;
  font-family: "Lucida Grande", "Lucida Sans Unicode", Helvetica, Arial, Verdana, sans-serif;
  font-size: 12px;
  padding: 0 20px;
  text-align: center;
  text-decoration: none;
}
#menu_class# a:hover {
  background: #f6f6f6;
  border-radius: 4px 4px 0 0;
  -moz-border-radius: 4px 4px 0 0;
  -webkit-border-radius: 4px 4px 0 0;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1);
  -moz-box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1);
  -webkit-box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1);
  position: relative;
  top: 0;
}
#menu_class# ul {
  background: #e5e5e5;
  border-radius: 5px 5px 0 0;
  -moz-border-radius: 5px 5px 0 0;
  -webkit-border-radius: 5px 5px 0 0;
  list-style: none;
}
#menu_class# > ul {
  background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAoCAIAAACw1AcgAAAAA3NCSVQICAjb4U/gAAAAHUlEQVQImWN4+vQpEwMDAxMDAwPT////4exBIgYA2loa5nAttNYAAAAASUVORK5CYII=) 100% 100%;
  background: -moz-linear-gradient(top, #e5e5e5 0%, #dddddd 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #e5e5e5), color-stop(100%, #dddddd));
  background: -webkit-linear-gradient(top, #e5e5e5 0%, #dddddd 100%);
  background: -o-linear-gradient(top, #e5e5e5 0%, #dddddd 100%);
  background: -ms-linear-gradient(top, #e5e5e5 0%, #dddddd 100%);
  background: linear-gradient(#e5e5e5 0%, #dddddd 100%);
  box-shadow: 0 2px 2px 1px rgba(0, 0, 0, 0.3);
  -moz-box-shadow: 0 2px 2px 1px rgba(0, 0, 0, 0.3);
  -webkit-box-shadow: 0 2px 2px 1px rgba(0, 0, 0, 0.3);
  padding-top: 5px;
  width: 100%;
}
#menu_class# > ul > li {
  float: left;
  padding: 0 10px;
  position: relative;
}
#menu_class# > ul > li:hover > a {
  background: #f6f6f6;
  border-radius: 4px 4px 0 0;
  -moz-border-radius: 4px 4px 0 0;
  -webkit-border-radius: 4px 4px 0 0;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1);
  -moz-box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1);
  -webkit-box-shadow: 0 2px 3px rgba(0, 0, 0, 0.1);
  position: relative;
}
#menu_class# > ul > li a {
  line-height: 35px;
}
#menu_class# > ul > li a:hover {
  box-shadow: none;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
}
#menu_class# .has-sub:hover ul {
  display: block;
}
#menu_class# .has-sub a {
  display: block;
  position: relative;
}
#menu_class# .has-sub > a:after {
  content: '';
  display: block;
  width: 10px;
  height: 9px;
  position: absolute;
  right: 5px;
  top: 50%;
  margin-top: -5px;
  background-image: url(data:image/png;base64,);
  -webkit-transform: rotate(360deg);
  -webkit-transform: rotate(360deg);
}
#menu_class# .has-sub ul {
  background: #f6f6f6;
  border: 1px solid #dddddd;
  border-radius: 5px;
  -moz-border-radius: 5px;
  -webkit-border-radius: 5px;
  display: none;
  padding: 10px 0;
  position: absolute;
  left: 50%;
  top: 34px;
  margin-left: -70px;
  width: [[sub_menu_width:140px]];
  z-index: 1;
}
#menu_class# .has-sub ul li:hover > a {
  background: #dddddd;
  color: @sub_menu_text_hover_color;
  border-color: #e5e5e5 transparent transparent transparent;
}
#menu_class# .has-sub ul a {
  line-height: 160%;
  padding: 8px 0;
}
#menu_class# .has-sub .has-sub a:after {
  background-image: url(data:image/png;base64,);
  -webkit-transform: rotate(360deg);
}
#menu_class# .has-sub .has-sub ul {
  background: #dddddd;
  left: 100%;
  top: 0;
  margin-left: 0;
}
#menu_class# .has-sub .has-sub ul a {
  background: none;
  box-shadow: none;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
  border-radius: 0;
  -moz-border-radius: 0;
  -webkit-border-radius: 0;
}
#menu_class# .has-sub .has-sub ul a:after {
  background-image: none;
}
#menu_class# .has-sub .has-sub ul a:hover {
  background: #bfbfbf;
}
