css:
@color: [[main_color:#4a5662]];
@darker: darken(@color, 10%);
@lighter: lighten(@color, 10%); //#606a76
@border1: darken(@lighter, 5%);

@charset "UTF-8";
#menu_class# ul,
#menu_class# li,
#menu_class# span,
#menu_class# a {
  border: 0;
  margin: 0;
  padding: 0;
  position: relative;
}
#menu_class# {
  border-radius: 5px;
  -moz-border-radius: 5px;
  -webkit-border-radius: 5px;
  background: #f2edea url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAA0CAIAAADEwMXAAAAAA3NCSVQICAjb4U/gAAAAMklEQVQImWP49PYV0////6GYAcFm+I9d/P9/JgZkcRR12NVDzMMihlMtRJyBkHpMNwIA6ZmLp7k56KwAAAAASUVORK5CYII=) 100% 100%;
  box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);
  -moz-box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);
  background: -moz-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #f2edea), color-stop(100%, #c0bebf));
  background: -webkit-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: -o-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: -ms-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: linear-gradient(to bottom, #f2edea 0%, #c0bebf 100%);
  font-weight: 600;
  height: 52px;
  width: [[menu_width:auto]];
}
#menu_class#:after,
#menu_class# ul:after {
  content: '';
  display: block;
  clear: both;
}
#menu_class# a {
  box-shadow: inset 0 1px 0 whitesmoke;
  -moz-box-shadow: inset 0 1px 0 whitesmoke;
  -webkit-box-shadow: inset 0 1px 0 whitesmoke;
  background: #f2edea url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAA0CAIAAADEwMXAAAAAA3NCSVQICAjb4U/gAAAAMklEQVQImWP49PYV0////6GYAcFm+I9d/P9/JgZkcRR12NVDzMMihlMtRJyBkHpMNwIA6ZmLp7k56KwAAAAASUVORK5CYII=) 100% 100%;
  background: -moz-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #f2edea), color-stop(100%, #c0bebf));
  background: -webkit-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: -o-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: -ms-linear-gradient(top, #f2edea 0%, #c0bebf 100%);
  background: linear-gradient(to bottom, #f2edea 0%, #c0bebf 100%);
  color: [[menu_text_color:#666666]];
  display: inline-block;
  font-family: Arial, Verdana, sans-serif;
  font-size: [[menu_font_size:12px]];
  line-height: 52px;
  padding: 0 28px;
  text-decoration: none;
}
#menu_class# ul {
  list-style: none;
  box-shadow: inset 0 1px 0 whitesmoke;
  -moz-box-shadow: inset 0 1px 0 whitesmoke;
  -webkit-box-shadow: inset 0 1px 0 whitesmoke;
}
#menu_class# > ul {
  float: left;
}
#menu_class# > ul > li {
  float: left;
}
#menu_class# > ul > li:first-child a {
  border-radius: 5px 0 0 5px;
  -moz-border-radius: 5px 0 0 5px;
  -webkit-border-radius: 5px 0 0 5px;
}
#menu_class# > ul > li.active a,
#menu_class# > ul > li:hover > a {
  box-shadow: inset 0 -2px 3px rgba(0, 0, 0, 0.15);
  -moz-box-shadow: inset 0 -2px 3px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: inset 0 -2px 3px rgba(0, 0, 0, 0.15);
  color: white;
  background: @color url(data:image/png;base64,);

  background: -moz-linear-gradient(top, @color 0%, @lighter 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, @color), color-stop(100%, @lighter));
  background: -webkit-linear-gradient(top, @color 0%, @lighter 100%);
  background: -o-linear-gradient(top, @color 0%, @lighter 100%);
  background: -ms-linear-gradient(top, @color 0%, @lighter 100%);
  background: linear-gradient(to bottom, @color 0%, @lighter 100%);

}
#menu_class# .has-sub {
  z-index: 1;
}
#menu_class# .has-sub:hover > ul {
  display: block;
}
#menu_class# .has-sub ul {
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
  -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
  display: none;
  position: absolute;
  width: [[sub_menu_width:200px]];
  top: 100%;
  left: 0;
}
#menu_class# .has-sub ul li a {
  background: @lighter;
  border-bottom: 1px solid #59636f;
  border-bottom: 1px solid @border1;
  box-shadow: inset 0 1px 0 @lighter;
  -moz-box-shadow: inset 0 1px 0 @lighter;
  -webkit-box-shadow: inset 0 1px 0 @lighter;
  color: white;
  display: block;
  line-height: 160%;
  padding: 15px 10px;
  font-size: [[sub_menu_font_size:12px]];
}
#menu_class# .has-sub ul li:hover a {
  background: @color;
  box-shadow: inset 0 0 3px 1px rgba(0, 0, 0, 0.15);
  -moz-box-shadow: inset 0 0 3px 1px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: inset 0 0 3px 1px rgba(0, 0, 0, 0.15);
}
#menu_class# .has-sub .has-sub:hover > ul {
  display: block;
}
#menu_class# .has-sub .has-sub ul {
  display: none;
  position: absolute;
  left: 100%;
  top: 0;
}
#menu_class# .has-sub .has-sub ul li a {
  background: @lighter;
  box-shadow: none;
  -moz-box-shadow: none;
  -webkit-box-shadow: none;
}
#menu_class# .has-sub .has-sub ul li a:hover {
  background: @color;
  box-shadow: inset 0 0 3px 1px rgba(0, 0, 0, 0.15);
  -moz-box-shadow: inset 0 0 3px 1px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: inset 0 0 3px 1px rgba(0, 0, 0, 0.15);
}
