/* Starter CSS for Flyout Menu */
#menu_class# {
  padding: 0;
  margin: 0;
  border: 0; }

#menu_class# ul, li {
  list-style: none;
  margin: 0;
  padding: 0; }

#menu_class# ul {
  position: relative;
  z-index: 597;
  float: left; }

#menu_class# ul li {
  float: left;
  min-height: 1px;
  line-height: 1em;
  vertical-align: middle; }

#menu_class# ul li.hover,
#menu_class# ul li:hover {
  position: relative;
  z-index: 599;
  cursor: default; }

#menu_class# ul ul {
  visibility: hidden;
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 598;
  width: 100%; }

#menu_class# ul ul li {
  float: none; }

#menu_class# ul li:hover > ul {
  visibility: visible; }

#menu_class# ul ul {
  top: 0;
  left: 100%; }

#menu_class# ul li {
  float: none; }

/* Custom Stuff */
#menu_class# {
  -moz-border-radius: 5px;
  -webkit-border-radius: 5px;
  border-radius: 5px;
  -moz-background-clip: padding;
  -webkit-background-clip: padding-box;
  background-clip: padding-box;
  -moz-box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);
  -webkit-box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);
  box-shadow: 0 0 2px 1px rgba(0, 0, 0, 0.15);
  width: [[menu_width:200px]]; 
}
  #menu_class# span, #menu_class# a {
    display: inline-block;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
    text-decoration: none; }
  #menu_class#:after, #menu_class# ul:after {
    content: '';
    display: block;
    clear: both; }
  #menu_class# > ul > li:first-child {
    -moz-border-radius: 5px 5px 0 0;
    -webkit-border-radius: 5px 5px 0 0;
    border-radius: 5px 5px 0 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box; }
  #menu_class# > ul > li:last-child {
    -moz-border-radius: 0 0 5px 5px;
    -webkit-border-radius: 0 0 5px 5px;
    border-radius: 0 0 5px 5px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box; }
  #menu_class# > ul > li ul ul {
    -moz-border-radius: 0 6px 6px 0;
    -webkit-border-radius: 0 6px 6px 0;
    border-radius: 0 6px 6px 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box; }
  #menu_class# > ul > li ul ul li:first-child {
    -moz-border-radius: 0 5px 0 0;
    -webkit-border-radius: 0 5px 0 0;
    border-radius: 0 5px 0 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box; }
  #menu_class# > ul > li ul ul li:last-child {
    -moz-border-radius: 0 0 5px 0;
    -webkit-border-radius: 0 0 5px 0;
    border-radius: 0 0 5px 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box; }
  #menu_class# ul, #menu_class# li {
    width: 100%; }
  #menu_class# li {
    background: #dddddd url(data:image/png;base64,) repeat-x;
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #e5e5e5), color-stop(1, #dddddd));
    background-image: -webkit-linear-gradient(top, #e5e5e5, #dddddd);
    background-image: -moz-linear-gradient(top, #e5e5e5, #dddddd);
    background-image: -ms-linear-gradient(top, #e5e5e5, #dddddd);
    background-image: -o-linear-gradient(top, #e5e5e5, #dddddd);
    background-image: linear-gradient(#e5e5e5, #dddddd); }
    #menu_class# li:hover {
      background: #f6f6f6; }
  #menu_class# a {
    color: #666666;
    line-height: 160%;
    padding: 11px 28px 11px 28px;
    width: 144px; }
  #menu_class# ul ul {
    width: [[sub_menu_width: 200px]];
    border: 1px solid #dddddd;
    -moz-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
    -webkit-box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15);
    box-shadow: 0 1px 2px rgba(0, 0, 0, 0.15); }
    #menu_class# ul ul li {
      background: #f6f6f6; }
      #menu_class# ul ul li:hover {
        background: #dddddd; }
        #menu_class# ul ul li:hover a {
          color: [[menu_background_color:#AE0001]]; }
      #menu_class# ul ul li ul li {
        background: #dddddd; }
        #menu_class# ul ul li ul li:hover {
          background: #b7b7b7; }
  #menu_class# .has-sub {
    position: relative; }
    #menu_class# .has-sub:after, #menu_class# .has-sub > ul > .has-sub:hover:after {
      content: '';
      display: block;
      width: 10px;
      height: 9px;
      position: absolute;
      right: 5px;
      top: 50%;
      margin-top: -5px;
      background-image: url(data:image/png;base64,);
      -moz-transform: rotate(360deg);
      -o-transform: rotate(360deg);
      -ms-transform: rotate(360deg);
      -webkit-transform: rotate(360deg);
      transform: rotate(360deg); }
    #menu_class# .has-sub > ul > .has-sub:after, #menu_class# .has-sub:hover:after {
      background-image: url(data:image/png;base64,); }
