css:
@import url(http://fonts.googleapis.com/css?family=Oxygen+Mono);

@menu_background_color : [[menu_background_color: #333]];
@color: [[main_color: #1b9bff]];
@darker: darken(@color, 10%);
@lighter: lighten(@color, 10%);

/* Starter CSS for Menu */
#menu_class# {
  padding: 0;
  margin: 0;
  border: 0; 
  width: [[menu_width:auto]];
}

#menu_class# ul, #menu_class# li {
  list-style: none;
  margin: 0;
  padding: 0; }

#menu_class# ul {
  position: relative;
  z-index: 597; }

#menu_class# ul li {
  float: left;
  min-height: 1px;
  vertical-align: middle; }

#menu_class# ul li.hover,
#menu_class# ul li:hover {
  position: relative;
  z-index: 599;
  cursor: default; }

#menu_class# ul ul {
  visibility: hidden;
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 598;
  width: 100%; 
}

#menu_class# ul ul li {
  float: none; }

#menu_class# ul ul ul {
  top: 0;
  left: [[sub_menu_width:190px]];
  width:  [[sub_menu_width:190px]]}

#menu_class# ul li:hover > ul {
  visibility: visible; }

#menu_class# ul ul {
  bottom: 0;
  left: 0; }

#menu_class# ul ul {
  margin-top: 0; }

#menu_class# ul ul li {
  font-weight: normal; }

#menu_class# a {
  display: block;
  line-height: 1em;
  text-decoration: none; }

/* Custom CSS Styles */
#menu_class# {
  background: @menu_background_color;
  border-bottom: 4px solid @color;
  font-family: 'Oxygen Mono', Tahoma, Arial, sans-serif;
  font-size: 12px; }
  #menu_class# > ul {
    *display: inline-block; }
  #menu_class#:after, #menu_class# ul:after {
    content: '';
    display: block;
    clear: both; }
  #menu_class# ul {
    text-transform: uppercase; }
    #menu_class# ul ul {
      border-top: 4px solid @color;
      text-transform: none;
      min-width: [[sub_menu_width:190px]]; }
      #menu_class# ul ul a {
        background: @color;
        color: [[sub_menu_text_color:#ffffff]];
        border: 1px solid @darker;
        border-top: 0 none;
        line-height: 150%;
        padding: 16px 20px; 
        font-size: [[sub_menu_font_size:12px]];
        }
      #menu_class# ul ul ul {
        border-top: 0 none; }
      #menu_class# ul ul li {
        position: relative; }
        #menu_class# ul ul li:first-child > a {
          border-top: 1px solid @darker; }
        #menu_class# ul ul li:hover > a {
          background: @lighter; 
          color: [[sub_menu_text_hover_color:#ffffff]];
         }
        #menu_class# ul ul li:last-child > a {
          -moz-border-radius: 0 0 3px 3px;
          -webkit-border-radius: 0 0 3px 3px;
          border-radius: 0 0 3px 3px;
          -moz-background-clip: padding;
          -webkit-background-clip: padding-box;
          background-clip: padding-box;
          -moz-box-shadow: 0 1px 0 @color;
          -webkit-box-shadow: 0 1px 0 @color;
          box-shadow: 0 1px 0 @color; }
        #menu_class# ul ul li:last-child:hover > a {
          -moz-border-radius: 0 0 0 3px;
          -webkit-border-radius: 0 0 0 3px;
          border-radius: 0 0 0 3px;
          -moz-background-clip: padding;
          -webkit-background-clip: padding-box;
          background-clip: padding-box; }
        #menu_class# ul ul li.has-sub > a:after {
          content: '+';
          position: absolute;
          top: 50%;
          right: 15px;
          margin-top: -8px; }
    #menu_class# ul li:hover > a, 
    #menu_class# ul li.active > a {
      background: @color;
      color: [[menu_text_hover_color:#ffffff]]; 
    }

    #menu_class# ul li.has-sub > a:after {
      content: '+';
      margin-left: 5px; }
    #menu_class# ul li.last ul {
      left: auto;
      right: 0; }
      #menu_class# ul li.last ul ul {
        left: auto;
        right: 99.5%; }
  #menu_class# a {
    background: @menu_background_color;
    color: [[menu_text_color:#CBCBCB]];
    padding: 0 20px; }
  #menu_class# > ul > li > a {
    line-height: 48px; 
    font-size: [[menu_font_size:12px]];
}
