/* CSS for Vertical Menu */
#menu_class# {
  padding: 0;
  margin: 0;
  border: 0; }

#menu_class# ul, #menu_class# li {
  list-style: none;
  margin: 0;
  padding: 0; }

#menu_class# ul {
  position: relative;
  z-index: 597; }

#menu_class# ul li {
  min-height: 1px;
  line-height: 1em; }

#menu_class# a {
  display: block;
  text-decoration: none; }

/* Custom CSS Styles */
#menu_class# {
  font-family: Helvetica, Arial, sans-serif;
  font-weight: bold;
  width: 160px; }
  #menu_class# ul {
    background: url(data:image/svg+xml;base64,);
    background-color: #F1F1F1;
    background-image: -webkit-linear-gradient(top, white, whitesmoke);
    background-image: -moz-linear-gradient(top, white, whitesmoke);
    background-image: -o-linear-gradient(top, white, whitesmoke);
    background-image: linear-gradient(white, whitesmoke);
    border: 1px solid #CCC;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box; }
  #menu_class# li {
    border-bottom: 1px solid #CCC;
    -moz-box-shadow: 0 1px 0 white;
    -webkit-box-shadow: 0 1px 0 white;
    box-shadow: 0 1px 0 white;
    padding: 1px; }
    #menu_class# li:last-child, #menu_class# li.last {
      -moz-box-shadow: none;
      -webkit-box-shadow: none;
      box-shadow: none;
      border-bottom: 0 none; }
    #menu_class# li.active {
      -moz-box-shadow: 1px 2px 0 -1px rgba(0, 0, 0, 0.25);
      -webkit-box-shadow: 1px 2px 0 -1px rgba(0, 0, 0, 0.25);
      box-shadow: 1px 2px 0 -1px rgba(0, 0, 0, 0.25);
      background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAuCAIAAABmjeQ9AAAAA3NCSVQICAjb4U/gAAAAQUlEQVQImV2NsQ3AMAzDCN3atX/mK3aI6xjZZEgmedcTMWiEiFEDdXc2ytju/uTdn61h8KQ4xbidzfkd/cfI7fwABsNToh7mpNgAAAAASUVORK5CYII=);
      background-image: -webkit-linear-gradient(top, #6dc864, #4cb841);
      background-image: -moz-linear-gradient(top, #6dc864, #4cb841);
      background-image: -o-linear-gradient(top, #6dc864, #4cb841);
      background-image: linear-gradient(#6dc864, #4cb841);
      position: relative;
      left: -8px;
      width: 170px; }
      #menu_class# li.active a {
        border: 1px dashed #90d58a;
        border: 1px dashed rgba(255, 255, 255, 0.25);
        border-right: 0 none;
        position: relative;
        color: #FFF;
        text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.3); }
      #menu_class# li.active:before, #menu_class# li.active:after {
        content: ''; }
      #menu_class# li.active:before {
        z-index: 1;
        width: 0px;
        height: 0px;
        position: absolute;
        left: 0;
        bottom: -5px;
        border-style: solid;
        border-width: 0 8px 5px 0;
        border-color: transparent #3c9234 transparent transparent; }
      #menu_class# li.active:after {
        background: url(data:image/png;base64,) no-repeat;
        display: block;
        height: 48px;
        width: 24px;
        position: absolute;
        right: -24px;
        top: 0px;
        z-index: 1; }
  #menu_class# a {
    border: 1px solid transparent;
    color: #444;
    height: 42px;
    line-height: 40px;
    padding: 0 12px;
    text-align: right;
    text-overflow: ellipsis; }
