css:
@import url(http://fonts.googleapis.com/css?family=Lato:300,400,700);
@charset "UTF-8";

@main_color :  [[main_color:#1b9bff]];
@menu_width : [[menu_width:auto]];
@menu_font_size : [[menu_font_size:13px]];
@sub_menu_width : [[sub_menu_width:200px]];
@sub_menu_font_size : [[sub_menu_font_size:13px]];
@sub_menu_text_color : [[sub_menu_text_color:#8b8b8b]];
@sub_menu_text_hover_color : [[sub_menu_text_hover_color:#5c5c5c]];
@sub_menu_background_color : [[sub_menu_background_color:#efefef]];


// Less Styles
@light-color: lighten(@main_color, 10%);
@dark-color: darken(@main_color, 10%);
@dark-color1: darken(@main_color, 20%);
@dark-color2: darken(@main_color, 30%);
@dark-color3: darken(@main_color, 40%);

.rounded-corners (@radius: 5px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  -ms-border-radius: @radius;
  -o-border-radius: @radius;
  border-radius: @radius;
}

.rounded-corner (@1, @2, @3, @4) {
  -webkit-border-radius: @1 @2 @3 @4;
  -moz-border-radius: @1 @2 @3 @4;
  -ms-border-radius: @1 @2 @3 @4;
  -o-border-radius: @1 @2 @3 @4;
  border-radius: @1 @2 @3 @4;
}

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background:-moz-linear-gradient(top, @top-color 0%, @bottom-color 100%); 
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background:-webkit-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:-o-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:-ms-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	filter:progid:DXImageTransform.Microsoft.gradient( startColorstr='@top-color', endColorstr='@bottom-color',GradientType=0 ); 
}

.box-shadow(@x, @y, @spread, @color) {
	-moz-box-shadow: @x @y @spread @color;
	-webkit-box-shadow:@x @y @spread @color;
	box-shadow: @x @y @spread @color;
}

.box-shadow-inset(@x, @y, @spread, @color) {
	-moz-box-shadow: inset @x @y @spread @color;
	-webkit-box-shadow: inset @x @y @spread @color;
	box-shadow: inset @x @y @spread @color;
}
.text-shadow(@x, @y, @spread, @color) {
	-webkit-text-shadow: @x @y @spread @color;
	-moz-text-shadow: @x @y @spread @color;

}
.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}


/* Base Styles */
#cssmenu > ul,
#cssmenu > ul li,
#cssmenu > ul ul {list-style: none; margin: 0; padding: 0;}
#cssmenu > ul {position: relative;z-index: 597; float: left;}
#cssmenu > ul li { float: left; min-height: 1px; line-height: 1.3em; vertical-align: middle; padding: 10px;}
#cssmenu > ul li.hover,
#cssmenu > ul li:hover {z-index: 599; cursor: default;}
#cssmenu > ul ul {visibility: hidden; position: absolute; top: 100%; left: 0; z-index: 598;}
#cssmenu > ul ul li {float: none;}
//#cssmenu > ul ul ul {top: 1px; left: 99%;}
#cssmenu > ul li:hover > ul {visibility: visible;}
/* Align last drop down RTL */


/* Theme Styles */
#cssmenu > ul a:link    { text-decoration: none;}
#cssmenu > ul a:active	{ color: #ffa500; }
#cssmenu li {
	padding: 0;
	color: #000;
}

#cssmenu {
	font-family: 'Lato', sans-serif;
	width: @menu_width; 
	.rounded-corners(3px); 
	background: @main_color;
	font-size: @menu_font_size;
	.box-shadow-inset(0, 2px, 2px, rgba(255,255,255,0.3));
}

#cssmenu > ul  {
	padding: 0 5px;
	.box-shadow-inset(0, -2px, 2px, rgba(0,0,0,0.3));
	.rounded-corners(3px); 
	display: block;
	float: none;
	.clearfix;
}
#cssmenu > ul > li {
	padding: 8px 5px;
}
#cssmenu > ul > li:hover {
}
#cssmenu > ul > li > a,
#cssmenu > ul > li > a:link, 
#cssmenu > ul > li > a:visited,{
	text-shadow: 0 -1px 1px @dark-color2;
	color: #fff;
	padding: 7px 20px;
	display: block;
	.rounded-corners(3px);
}
#cssmenu > ul > li > a:hover,
#cssmenu > ul > li:hover > a {
	background-color: @dark-color;		
}

#cssmenu li li a {
	color: @sub_menu_text_color;
	font-size: @sub_menu_font_size;
}
#cssmenu li li a:hover {
	color: @sub_menu_text_hover_color;
	border-color: @sub_menu_text_hover_color;
}

#cssmenu ul ul {
	margin: 0 10px;
	padding: 0 10px; 
	float: none;
	background: @sub_menu_background_color;
	border: 2px solid @main_color;
	border-top: none;
	right: 0;
	left: 0;
	.rounded-corner(0, 0, 3px, 3px);
	.box-shadow(0, 2px, 3px, rgba(0,0,0,.2));
}
#cssmenu ul > li > ul > li {
 	margin: 0 10px 0 0; 
	position: relative;
 	padding: 0;
	float: left;
}
#cssmenu ul > li > ul > li > a{
	padding: 10px 20px 10px 10px;
	display: block;
}
#cssmenu ul > li > ul > li.has-sub > a:before {
content: '';
position: absolute;
top: 18px;
right: 6px;
border: 5px solid transparent;
border-top: 5px solid @sub_menu_text_color;
}
#cssmenu ul > li > ul > li.has-sub > a:hover:before {
	border-top: 5px solid @sub_menu_text_hover_color;
}

#cssmenu ul ul ul {
	width: @sub_menu_width;
	top: 100%;
	border: 2px solid @main_color;	
}
#cssmenu ul ul ul li {
	float: none;
	
}
