css:
@menu_width: [[menu_width: auto]];
@menu_font_size : [[menu_font_size : 12px]];
@menu_text_color : [[menu_text_color : #000000]];
@menu_text_hover_color : [[menu_text_hover_color : #000000]];
@menu_background_color: [[menu_background_color: #ffffff]];


@sub_menu_width: [[sub_menu_width: 200px]];
@sub_menu_font_size : [[sub_menu_font_size : 11px]];
@sub_menu_text_color: [[sub_menu_text_color: #ffffff]];
@sub_menu_text_hover_color: [[sub_menu_text_hover_color: #ffffff]];
@main_color: [[main_color: #db000b]];

@color_stop: lighten(@menu_background_color, 12%);
@hover_color_stop1: darken(@menu_background_color, 5%);
@hover_color_stop2: lighten(@menu_background_color, 7%);
@shadow: darken(@menu_background_color, 15%);

@submenu_dark: darken(@main_color, 10%);
@submenu_dark2: darken(@main_color, 15%);
@submenu_light: lighten(@main_color, 10%);

#cssmenu ul,
#cssmenu li,
#cssmenu span,
#cssmenu a {
  margin: 0;
  padding: 0;
  position: relative;
}

#cssmenu {
  line-height: 1;
  border-radius: 5px 5px 0 0;
  -moz-border-radius: 5px 5px 0 0;
  -webkit-border-radius: 5px 5px 0 0;
  background: @menu_background_color;
  background: -moz-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, @color_stop), color-stop(100%, @menu_background_color));
  background: -webkit-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: -o-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: -ms-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: linear-gradient(to bottom, @color_stop 0%, @menu_background_color 100%);
  border-bottom: 2px solid @main_color;
  width: @menu_width;
}
#cssmenu:after,
#cssmenu ul:after {
  content: '';
  display: block;
  clear: both;
}
#cssmenu a {
  background: @menu_background_color;
  background: -moz-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, @color_stop), color-stop(100%, @menu_background_color));
  background: -webkit-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: -o-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: -ms-linear-gradient(top, @color_stop 0%, @menu_background_color 100%);
  background: linear-gradient(to bottom, @color_stop 0%, @menu_background_color 100%);
  color: #ffffff;
  display: block;
  font-family: Helvetica, Arial, Verdana, sans-serif;
  padding: 19px 20px;
  text-decoration: none;
}
#cssmenu ul {
  list-style: none;
}
#cssmenu > ul {
  
}
#cssmenu > ul > li {
  display: inline-block;
  float: left;
  margin: 0;
}

#cssmenu.align-center {
  text-align: center;
}

#cssmenu.align-center > ul > li {
  float: none;
}

#cssmenu.align-center ul ul {
  text-align: left;
}

#cssmenu.align-right > ul {
  float: right;
}

#cssmenu.align-right ul ul {
  text-align: right;
}

#cssmenu > ul > li > a {
  color: @menu_text_color;
  font-size: @menu_font_size;
}
#cssmenu > ul > li:hover:after {
  content: '';
  display: block;
  width: 0;
  height: 0;
  position: absolute;
  left: 50%;
  bottom: 0;
  border-left: 10px solid transparent;
  border-right: 10px solid transparent;
  border-bottom: 10px solid @main_color;
  margin-left: -10px;
}
#cssmenu > ul > li:first-child > a {
  border-radius: 5px 0 0 0;
  -moz-border-radius: 5px 0 0 0;
  -webkit-border-radius: 5px 0 0 0;
}

#cssmenu.align-right > ul > li:first-child > a,
#cssmenu.align-center > ul > li:first-child > a {
  border-radius: 0;
  -moz-border-radius: 0;
  -webkit-border-radius: 0;
}
#cssmenu.align-right > ul > li:last-child > a {
  border-radius: 0 5px 0 0;
  -moz-border-radius: 0 5px 0 0;
  -webkit-border-radius: 0 5px 0 0;
}
#cssmenu > ul > li.active > a,
#cssmenu > ul > li:hover > a {
  color: @menu_text_hover_color;
  box-shadow: inset 0 0 3px @shadow;
  -moz-box-shadow: inset 0 0 3px @shadow;
  -webkit-box-shadow: inset 0 0 3px @shadow;
  background: @hover_color_stop1;
  background: -moz-linear-gradient(top, @hover_color_stop2 0%, @hover_color_stop1 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, @hover_color_stop2), color-stop(100%, @hover_color_stop1));
  background: -webkit-linear-gradient(top, @hover_color_stop2 0%, @hover_color_stop1 100%);
  background: -o-linear-gradient(top, @hover_color_stop2 0%, @hover_color_stop1 100%);
  background: -ms-linear-gradient(top, @hover_color_stop2 0%, @hover_color_stop1 100%);
  background: linear-gradient(to bottom, @hover_color_stop2 0%, @hover_color_stop1 100%);
}

#cssmenu .has-sub {
  z-index: 1;
}
#cssmenu .has-sub:hover > ul {
  display: block;

}
#cssmenu .has-sub ul {
  display: none;
  position: absolute;
  width: @sub_menu_width;
  top: 100%;
  left: 0;
}
#cssmenu.align-right .has-sub ul {
  left: auto;
  right: 0;
}

#cssmenu .has-sub ul li {
  *margin-bottom: -1px;
}
#cssmenu .has-sub ul li a {
  background: @main_color;
  border-bottom: 1px dotted @submenu_light;
  font-size: @sub_menu_font_size;
  filter: none;
  display: block;
  line-height: 120%;
  padding: 10px;
  color: @sub_menu_text_color;
}
#cssmenu .has-sub ul li:hover a {
  background: @submenu_dark;
}

#cssmenu ul ul li:hover>a {
  color: @sub_menu_text_hover_color;
}
#cssmenu .has-sub .has-sub:hover > ul {
  display: block;
}
#cssmenu .has-sub .has-sub ul {
  display: none;
  position: absolute;
  left: 100%;
  top: 0;
}

#cssmenu.align-right .has-sub .has-sub ul,
#cssmenu.align-right ul ul ul {
  left: auto;
  right: 100%;
}

#cssmenu .has-sub .has-sub ul li a {
  background: @submenu_dark;
  border-bottom: 1px dotted @submenu_light;
}
#cssmenu .has-sub .has-sub ul li a:hover {
  background: @submenu_dark2;
}

#cssmenu ul ul li.last>a,
#cssmenu ul ul li:last-child>a,
#cssmenu ul ul ul li.last>a,
#cssmenu ul ul ul li:last-child>a,
#cssmenu .has-sub ul li:last-child>a,
#cssmenu .has-sub ul li.last>a
 {
  border-bottom: 0;
}
