css:
@charset "UTF-8";
@main_color :  [[main_color:#3fa338]];
@menu_width : [[menu_width:auto]];
@menu_font_size : [[menu_font_size:14px]];
@menu_text_color : [[menu_text_color:white]];
@menu_text_hover_color : [[menu_text_hover_color:white]];
@menu_background_color : [[menu_background_color:#1e1e1e]];
@sub_menu_width :[[sub_menu_width:150px]];
@sub_menu_font_size : [[sub_menu_font_size:12px]];
@sub_menu_text_color : [[sub_menu_text_color:#9A9A9A]];
@sub_menu_text_hover_color : [[sub_menu_text_hover_color:#9A9A9A]];
@sub_menu_background_color : [[sub_menu_background_color:white]];
@sub_menu_border_color : [[sub_menu_border_color:#EBEBEB]];


// Less Styles
@light-color: lighten(@main_color, 10%);
@dark-color: darken(@menu_background_color, 10%);

.rounded-corners (@radius: 5px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  border-radius: @radius;
}

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background:-moz-linear-gradient(top, @top-color 0%, @bottom-color 100%); 
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background:-webkit-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:linear-gradient(top, @top-color 0%,@bottom-color 100%); 
}

.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}

/* Base Styles */
#menu_class# > ul,
#menu_class# > ul li,
#menu_class# > ul ul {
 list-style: none;
 margin: 0;
 padding: 0;
}
#menu_class# > ul {
 position: relative;
 z-index: 597;
}
#menu_class# > ul li {
 float: left;
 min-height: 1px;
 line-height: 1.3em;
 vertical-align: middle;
}
#menu_class# > ul li.hover,
#menu_class# > ul li:hover {
 position: relative;
 z-index: 599;
 cursor: default;
}
#menu_class# > ul ul {
 visibility: hidden;
 position: absolute;
 top: 100%;
 left: 0;
 z-index: 598;
 width: 100%;
}
#menu_class# > ul ul li {
 float: none;
}
#menu_class# > ul ul ul {
 top: 1px;
 left: 99%;
}
#menu_class# > ul li:hover > ul {
 visibility: visible;
}

/* Align last drop down RTL */
#menu_class# > ul  > li.last ul ul {
 left: auto !important;
 right: 99%;
}
#menu_class# > ul  > li.last ul {
 left: auto;
 right: 0;
}
#menu_class# > ul  > li.last{
 text-align: right;
}


/* Theme Styles */
#menu_class# > ul  {
	border-top: 4px solid @main_color;
	font-family: Calibri, Tahoma, Arial, sans-serif;
	font-size: @menu_font_size;
	.linear-gradient(@menu_background_color, @dark-color);
        width: @menu_width;
        .clearfix();
	li {
		
		a { display: inline-block; padding: 10px 22px; }
	}
        > li {&.active, &.active:hover { background-color: @main_color; }}
	>li > a:link, >li > a:active, >li > a:visited { color: @menu_text_color; }
        >li >a:hover { color: @menu_text_hover_color; }

}

#menu_class# > ul ul ul { top: 0; }

#menu_class# > ul li li {
	background-color: @sub_menu_background_color;
	border-bottom: 1px solid @sub_menu_border_color;
	font-size: @sub_menu_font_size;
}

#menu_class# > ul li.hover,
#menu_class# > ul li:hover {
	background-color: #F5F5F5;
}

#menu_class# > ul > li.hover,
#menu_class# > ul > li:hover {
	background-color: @main_color;
	-webkit-box-shadow: inset 0 -3px 0 rgba(0,0,0,0.15);
	-moz-box-shadow: inset 0 -3px 0 rgba(0,0,0,0.15);
	box-shadow: inset 0 -3px 0 rgba(0,0,0,0.15);
}

#menu_class# > ul a:link,
#menu_class# > ul a:visited	{ color: @sub_menu_text_color; text-decoration: none; }
#menu_class# > ul a:hover		{ color: @sub_menu_text_hover_color; }
#menu_class# > ul a:active	{ color: @sub_menu_text_color; }

#menu_class# > ul ul {
	border: 1px solid #CCC \9; //IE's w/o box-shadow support
	-webkit-box-shadow: 0 0px 2px 1px  rgba(0,0,0,0.15);
	-moz-box-shadow: 0 0px 2px  1px rgba(0,0,0,0.15);
	box-shadow: 0 0px 2px  1px rgba(0,0,0,0.15);
	width: @sub_menu_width;
}
