css:
@charset "UTF-8";

@main_color :  [[main_color:#f23f37]];
@menu_width : [[menu_width:150px]]; // Check
@menu_font_size : [[menu_font_size:13px]]; // Check
@menu_text_color : [[menu_text_color:#e4433c]]; // Check
@menu_text_hover_color : [[menu_text_hover_color:white]]; // Check
@menu_background_color : [[menu_background_color:#eaeaea]]; // Check
@sub_menu_width : [[sub_menu_width:150px]]; // Check
@sub_menu_font_size : [[sub_menu_font_size:12px]]; // Check
@sub_menu_border_color : [[sub_menu_border_color:#d9d9d9]]; // Check

/* Some stylesheet reset */
#menu_class# > ul {
	list-style: none;
	margin: 0; padding: 0;
	vertical-align: baseline;
	line-height: 1;
}

/* The container */
#menu_class# > ul{
	display: block;
	position: relative;
	width: @menu_width;
}

	/* The list elements which contain the links */
	#menu_class# > ul li {
		display: block;
		position: relative;
		margin: 0;
		padding: 0;
		width: 100%;	
	}

		/* General link styling */
		#menu_class# > ul li a {
			/* Layout */
			display: block;
			position: relative;
			margin: 0;
			border-top: 1px dotted #fff;
			border-bottom: 1px dotted @sub_menu_border_color;
			padding: 11px 20px;

			/* Typography */
			font-family:  Helvetica, Arial, sans-serif;
			color: @menu_text_color;
			text-decoration: none;
			text-transform: uppercase;
			text-shadow: 0 1px 0 #fff;
			font-size: @menu_font_size;
			font-weight: 300;

			/* Background & effects */
			background: @menu_background_color;
		}

		/* Rounded corners for the first link of the menu/submenus */
		#menu_class# > ul li:first-child>a {
			border-top-left-radius: 4px;
			border-top-right-radius: 4px;
			border-top: 0;
		}

		/* Rounded corners for the last link of the menu/submenus */
		#menu_class# > ul li:last-child>a {
			border-bottom-left-radius: 4px;
			border-bottom-right-radius: 4px;
			border-bottom: 0;
		}


		/* The hover state of the menu/submenu links */
		#menu_class# > ul li a:hover, #menu_class# > ul li:hover>a {
			color: @menu_text_hover_color;
			text-shadow: 0 1px 0 rgba(0, 0, 0, .25);
			background: @main_color;
			background: -webkit-linear-gradient(@main_color, darken(@main_color,10%)); 
			background: -moz-linear-gradient(@main_color, darken(@main_color,10%));
			background: linear-gradient(@main_color, darken(@main_color,10%));
			border-color: transparent;
		}

		/* The arrow indicating a submenu */
		#menu_class# > ul .has-sub >a::after {
			content: "";
			position: absolute;
			top: 16px; right: 10px;
			width: 0px; height: 0px;

			/* Creating the arrow using borders */
			border: 4px solid transparent;
			border-left: 4px solid @menu_text_color; 
		}

		/* The same arrow, but with a darker color, to create the shadow effect */
		#menu_class# > ul .has-sub >a::before {
			content: "";
			position: absolute;
			top: 17px; right: 10px;
			width: 0px; height: 0px;

			/* Creating the arrow using borders */
			border: 4px solid transparent;
			border-left: 4px solid #fff;
		}

		/* Changing the color of the arrow on hover */
		#menu_class# > ul li>a:hover::after, #menu_class# > ul li:hover>a::after {
			border-left: 4px solid #fff;
		}

		#menu_class# > ul li>a:hover::before, #menu_class# > ul li:hover>a::before {
			border-left: 4px solid rgba(0, 0, 0, .25);
		}

	
		/* THE SUBMENUS */
		#menu_class# > ul ul {
			position: absolute;
			left: 100%; top: -9999px;
			padding-left: 5px;
			opacity: 0;
			width: @sub_menu_width;
			/* The fade effect, created using an opacity transition */
			-webkit-transition: opacity .3s ease-in;
			-moz-transition: opacity .3s ease-in;
			transition: opacity .3s ease-in;

			li a { font-size: @sub_menu_font_size;}
		}

		/* Showing the submenu when the user is hovering the parent link */
		#menu_class# > ul li:hover>ul {
			top: 0px; opacity: 1; z-index: 1;
		}
