css:
@charset "UTF-8";
@main_color :  [[main_color:#A80329]];
@menu_width : [[menu_width: auto]];
@menu_font_size : [[menu_font_size:14px]];
@menu_text_color : [[menu_text_color:#666]];
@menu_text_hover_color : [[menu_text_hover_color:black]];

@sub_menu_width : [[sub_menu_width:225px]];
@sub_menu_font_size : [[sub_menu_font_size:12px]];

#menu_class# {
	border:none;
	border:0px;
	margin:0px;
	padding:0px;
	font-family:verdana,geneva,arial,helvetica,sans-serif;
	font-size: @menu_font_size;
	font-weight:bold;
	color:#8e8e8e;
    width: @menu_width;
	
	>ul { margin-top: 6px !important; }
}

#menu_class# ul {
	background: #CDCDCD;
	background: -webkit-linear-gradient(#CDCDCD 0%, #E2E2E2 80%, #CDCDCD 100%);
	background: linear-gradient(#CDCDCD 0%, #E2E2E2 80%, #CDCDCD 100%);
	border-top: 1px solid #A8A8A8;
	-webkit-box-shadow: 
		inset 0 1px 0 #E9E9E9, 
		0 1px 0 @main_color, 
		0 2px 0 desaturate(lighten(@main_color,8%), 23%), 
		0 8px 0 @main_color, 
		0 9px 0 darken(@main_color,9%), 
		0 -1px 1px rgba(0,0,0,0.1);
	-moz-box-shadow: 
		inset 0 1px 0 #E9E9E9, 
		0 1px 0 @main_color, 
		0 2px 0 desaturate(lighten(@main_color,8%), 23%), 
		0 8px 0 @main_color, 
		0 9px 0 darken(@main_color,9%), 
		0 -1px 1px rgba(0,0,0,0.1);
	box-shadow: 
		inset 0 1px 0 #E9E9E9, 
		0 1px 0 @main_color, 
		0 2px 0 desaturate(lighten(@main_color,8%), 23%), 
		0 8px 0 @main_color, 
		0 9px 0 darken(@main_color,9%), 
		0 -1px 1px rgba(0,0,0,0.1);
	height:27px;
	list-style:none;
	margin:0;
	padding:0;
	
	ul { 
	border-top: 6px solid @main_color; 
	-webkit-box-shadow: none; 
	-moz-box-shadow: none; 
	box-shadow: none; 
	
		a { line-height:43px; }
		
		ul { left: 100%; top: 0; }
	}
}
	
	#menu_class# li{
		float:left;
		padding:0px 8px 0px 8px;
		}

	#menu_class# li a{
		color:@menu_text_color;
		display:block;
		font-weight:bold;
		line-height:30px;
		padding:0px 25px;
		text-align:center;
		text-decoration:none;
		}

		#menu_class# li a:hover{
			color:@menu_text_hover_color;
			text-decoration:none;
			}

	#menu_class# li ul{
		background:#e0e0e0;
		border-left:2px solid @main_color;
		border-right:2px solid @main_color;
		border-bottom:2px solid @main_color;
		display:none;
		height:auto;
		filter:alpha(opacity=95);
		opacity:0.95;
		position:absolute;
		width: @sub_menu_width;
		z-index:200;
		/*top:1em;
		/*left:0;*/
		}

	#menu_class# li:hover > ul{
		display:block;
		}

	#menu_class# li li {
		display:block;
		float:none;
		padding:0px;
		position: relative;
		width: @sub_menu_width;
		}

	#menu_class# li ul a{
		display:block;
		font-size: @sub_menu_font_size;
		font-style:normal;
		padding:0px 10px 0px 15px;
		text-align:left;
		}

		#menu_class# li ul a:hover{
			background:#949494;
			color:@menu_text_hover_color;
			opacity:1.0;
			filter:alpha(opacity=100);
			}

	#menu_class# p{ clear:left; }	

	#menu_class# .active > a{
		background:@main_color;
		-webkit-box-shadow: 0 -4px 0 @main_color, 0 -5px 0 desaturate(lighten(@main_color,8%), 23%), 0 -6px 0 @main_color;
		-moz-box-shadow: 0 -4px 0 @main_color, 0 -5px 0 desaturate(lighten(@main_color,8%), 23%), 0 -6px 0 @main_color;
		box-shadow: 0 -4px 0 @main_color, 0 -5px 0 desaturate(lighten(@main_color,8%), 23%), 0 -6px 0 @main_color;
		color:#ffffff;
		}

	#menu_class# .active > a:hover { color:white; }
