css:
@charset "UTF-8";
/* Starter CSS for Flyout Menu */

@main_color: [[main_color: #0fa1e0]];
@menu_width: [[menu_width: 200px]];
@menu_text_color: [[menu_text_color:#fff]];
@menu_text_hover_color: [[menu_text_hover_color:#fff]];
@menu_font_size: [[menu_font_size:12px]];

@sub_menu_width: [[sub_menu_width: 150px]];
@sub_menu_font_size: [[sub_menu_font_size:12px]];
@sub_menu_text_color: [[sub_menu_text_color: #fff]];

#menu_class# { padding: 0; margin: 0; border: 0; }

#menu_class# ul,
#menu_class# ul li,
#menu_class# ul ul {
  list-style: none;
  margin: 0; padding: 0;
}
#menu_class# ul {
  position: relative;
  z-index: 597;
  float: left;
  width: @menu_width;
}
#menu_class# ul li {
  float: left;
  min-height: 1px;
  line-height: 1em;
  vertical-align: middle;
}
#menu_class# ul li.hover,
#menu_class# ul li:hover {
  position: relative;
  z-index: 599;
  cursor: default;
}
#menu_class# ul ul {
  visibility: hidden;
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 598;
  width: 100%;
}
#menu_class# ul ul li {
  float: none;
}
#menu_class# ul ul ul {
  top: 1px; left: 99%;
}
#menu_class# ul li:hover > ul {
  visibility: visible;
}
#menu_class# ul ul {
  top: 1px; left: 99%;
}
#menu_class# ul li {
  float: none;
}
#menu_class# ul {
  font-weight: bold;
}
#menu_class# ul ul {
  margin-top: 1px;
}
#menu_class# ul ul li {
  font-weight: normal;
}
/* Custom CSS Styles */

#menu_class# {
  border-radius: 5px 0 0 5px;
  -moz-border-radius: 5px 0 0 5px;
  -webkit-border-radius: 5px 0 0 5px;
  background-color: #141414;
  background-image: url(data:image/png;base64,);
  background-repeat: repeat-x;
  width: @menu_width;
  
  &:after {
	  content: '';
	  display: block;
	  clear: both;
	}
}
#menu_class# a {
  background-color: #141414;
  background-image: url(data:image/png;base64,);
  background-repeat: repeat-x;
  color: @menu_text_color;
  display: block;
  font-family: Helvetica, Arial, Verdana, sans-serif;
  font-size: @menu_font_size;
  font-weight: normal;
  line-height: 180%;
  padding: 13px 20px;
  text-decoration: none;
}
#menu_class# ul {
  border-right: 2px solid @main_color;
  list-style: none;
}
#menu_class# > ul { float: left; }

#menu_class# > ul > li:hover:after {
  content: '';
  display: block;
  width: 0;
  height: 0;
  position: absolute;
  right: 0; top: 50%;
  border-top: 10px solid transparent;
  border-bottom: 10px solid transparent;
  border-right: 10px solid @main_color;
  margin-top: -10px;
}
#menu_class# > ul > li:first-child >a {
  border-radius: 5px 0 0 0;
  -moz-border-radius: 5px 0 0 0;
  -webkit-border-radius: 5px 0 0 0;
}
#menu_class# > ul > li:last-child > a {
  border-radius: 0 0 0 5px;
  -moz-border-radius: 0 0 0 5px;
  -webkit-border-radius: 0 0 0 5px;
}
#menu_class# > ul > li.active a {
  background-color: #070707;
  background-image: url(data:image/png;base64,);
  background-repeat: repeat-x;
}
#menu_class# > ul > li.active > a {
  box-shadow: inset 0 0 5px black;
  -moz-box-shadow: inset 0 0 5px black;
  -webkit-box-shadow: inset 0 0 5px black;
}

#menu_class# > ul > li:hover > a {
  background-color: #070707;
  background-image: url(data:image/png;base64,);
  background-repeat: repeat-x;
  box-shadow: inset 0 0 5px black;
  -moz-box-shadow: inset 0 0 5px black;
  -webkit-box-shadow: inset 0 0 5px black;
  color: @menu_text_hover_color;
}
#menu_class# .has-sub {
  z-index: 1;
}
#menu_class# .has-sub:hover > ul {
  display: block;
}
#menu_class# .has-sub ul {
  display: none;
  position: absolute;
  width: @sub_menu_width;
  top: 5px;
  left: 100%;

  a { font-size: @sub_menu_font_size; }
}
#menu_class# .has-sub ul li {
  *margin-bottom: -1px;
}
#menu_class# .has-sub ul li a {
  background: @main_color;
  border-bottom: 1px dotted lighten(@main_color,10%);
  filter: none;
  font-size: 11px;
  display: block;
  line-height: 120%;
  padding: 10px;
  color: @sub_menu_text_color;
}
#menu_class# .has-sub ul li:hover a {
  background: darken(@main_color,9%);
}
#menu_class# .has-sub .has-sub:hover > ul {
  display: block;
}
#menu_class# .has-sub .has-sub ul {
  display: none;
  position: absolute;
  left: 100%; top: 0;
}
#menu_class# .has-sub .has-sub ul li a {
  background: darken(@main_color,9%);
  border-bottom: 1px dotted @main_color;
}
#menu_class# .has-sub .has-sub ul li a:hover {
  background: darken(@main_color,18%);
}
