css:
@charset "UTF-8";
@import url(http://fonts.googleapis.com/css?family=Lato);

@main_color :  [[main_color:#709400]];
@menu_width : [[menu_width:200px]];
@menu_font_size : [[menu_font_size:13px]];
@menu_text_color : #fff;
@menu_text_hover_color : #000;
@menu_background_color : [[menu_background_color:#4c4e53]];
@menu_border_color : #000;
@sub_menu_width : 200px;
@sub_menu_font_size : 13px;
@sub_menu_text_color : #000;
@sub_menu_text_hover_color : #000;
@sub_menu_background_color : #000;
@sub_menu_border_color : #000;

// Less Styles
@light-color: lighten(@main_color, 10%);
@dark-color: darken(@main_color, 10%);
@dark-color1: darken(@main_color, 20%);
@dark-color2: darken(@main_color, 30%);
@dark-color3: darken(@main_color, 40%);

.rounded-corners (@radius: 5px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  border-radius: @radius;
}

.rounded-corner (@1, @2, @3, @4) {
  -webkit-border-radius: @1 @2 @3 @4;
  -moz-border-radius: @1 @2 @3 @4;
  border-radius: @1 @2 @3 @4;
}

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background: -moz-linear-gradient(@top-color 0%, @bottom-color 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background: -webkit-linear-gradient(@top-color 0%,@bottom-color 100%);  
	background: linear-gradient(@top-color 0%, @bottom-color 100%); 
}

.box-shadow(@x, @y, @spread, @color) {
	-moz-box-shadow: @x @y @spread @color;
	-webkit-box-shadow:@x @y @spread @color;
	box-shadow: @x @y @spread @color;
}

.box-shadow-inset(@x, @y, @spread, @color) {
	-moz-box-shadow: inset @x @y @spread @color;
	-webkit-box-shadow: inset @x @y @spread @color;
	box-shadow: inset @x @y @spread @color;
}
.text-shadow(@x, @y, @spread, @color) { text-shadow: @x @y @spread @color; }

.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}


/* Base Styles */
#cssmenu,
#cssmenu ul,
#cssmenu li,
#cssmenu a {
	margin: 0; 
	padding: 0; 
	border: 0; 
	list-style: none; 
	font-weight: normal;
	text-decoration: none;
	line-height: 1; 
	font-family: 'Lato', sans-serif;
	font-size: 14px;
	position: relative;
}
#cssmenu a {
	line-height: 1.3;
	padding: 6px 15px;	
}

#cssmenu {
	width: @menu_width;
}
#cssmenu > ul > li {    
	cursor: pointer;
	background: #000;
	border-bottom: 1px solid @menu_background_color;
}
#cssmenu > ul > li:last-child {
	border-bottom: 1px solid #3e3d3c;
}
#cssmenu > ul > li > a  {
	font-size: @menu_font_size;

	display: block;
	color: @menu_text_color; 
	text-shadow: 0 1px 1px #000;
	.linear-gradient(lighten(@menu_background_color,10%), @menu_background_color);
}
#cssmenu > ul > li > a:hover {
	text-decoration: none;
}

#cssmenu > ul > li.active {
	border-bottom: none;
}
#cssmenu > ul > li.active > a {
	.linear-gradient(lighten(@main_color, 10%), @main_color);
	color: #4e5800;
	text-shadow: 0 1px 1px @main_color;	
}

#cssmenu > ul > li.has-sub > a:after{
	content:""; 
	position:absolute; 
	top: 10px; 
	right: 10px; 
	border: 5px solid transparent; 
	border-left: 5px solid @menu_text_color; 
}
#cssmenu > ul > li.has-sub.active > a:after{
	right: 14px; 
	top: 12px; 
	border: 5px solid transparent; 
	border-top: 5px solid #4e5800; 

}
/* Sub menu */
#cssmenu ul ul {
  padding: 0;

   display: none;
}
#cssmenu ul ul a {
	background: #efefef;
	display: block;
	color: #797979;
	font-size: @sub_menu_font_size;
}
#cssmenu ul ul li {
	border-bottom: 1px solid #c9c9c9;		
}
#cssmenu ul ul li.odd a {
	background: #e5e5e5;
}
#cssmenu ul ul li:last-child{
	border: none;
}

jquery:
$('#cssmenu ul ul li:odd').addClass('odd');
$('#cssmenu ul ul li:even').addClass('even');
$('#cssmenu > ul > li > a').click(function() {
  $('#cssmenu li').removeClass('active');
  $(this).closest('li').addClass('active');	
  var checkElement = $(this).next();
  if((checkElement.is('ul')) && (checkElement.is(':visible'))) {
    $(this).closest('li').removeClass('active');
    checkElement.slideUp('normal');
  }
  if((checkElement.is('ul')) && (!checkElement.is(':visible'))) {
    $('#cssmenu ul ul:visible').slideUp('normal');
    checkElement.slideDown('normal');
  }
  if($(this).closest('li').find('ul').children().length == 0) {
    return true;
  } else {
    return false;	
  }		
});
