@import url(http://fonts.googleapis.com/css?family=Lato:300,400,700);
@main_color : [[main_color:#3dbd99]];
@menu_width : [[menu_width:200px]];
@menu_font_size : [[menu_font_size:18px]];
@menu_text_color : [[menu_text_color:#848889]];
@menu_border_color : [[menu_border_color:#d7d8da]];
@sub_menu_width : [[sub_menu_width:180px]];
@sub_menu_font_size : [[sub_menu_font_size:14px]];

// Less Styles
@light-color: lighten(@main_color, 10%);
@dark-color: darken(@main_color, 10%);

.rounded-corners (@radius: 5px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  -ms-border-radius: @radius;
  -o-border-radius: @radius;
  border-radius: @radius;
}

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background:-moz-linear-gradient(top, @top-color 0%, @bottom-color 100%); 
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background:-webkit-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:-o-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:-ms-linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	background:linear-gradient(top, @top-color 0%,@bottom-color 100%); 
	filter:progid:DXImageTransform.Microsoft.gradient( startColorstr='@top-color', endColorstr='@bottom-color',GradientType=0 ); 
}


/* Starter CSS for Flyout Menu */
#cssmenu,
#cssmenu ul,
#cssmenu ul li,
#cssmenu ul ul {list-style: none; margin: 0; padding: 0; border: 0;}
#cssmenu ul {position: relative; z-index: 597; float: left;}
#cssmenu ul li { float: left; min-height: 1px; line-height: 1em; vertical-align: middle;}
#cssmenu ul li.hover,
#cssmenu ul li:hover {position: relative; z-index: 599; cursor: default;}
#cssmenu ul ul {margin-top: 1px; visibility: hidden; position: absolute; top: 1px; left: 99%; z-index: 598; width: 100%;}
#cssmenu ul ul li {float: none;}
#cssmenu ul ul ul {top: 1px; left: 99%;}
#cssmenu ul li:hover > ul { visibility: visible;}

#cssmenu ul li {float: none;}
#cssmenu ul ul li {font-weight: normal;}


/* Custom CSS Styles */
#cssmenu {
  font-family: 'Lato', sans-serif;
  font-size: @menu_font_size;
  width: @menu_width;
}
#cssmenu ul a,
#cssmenu ul a:link,
#cssmenu ul a:visited	{
  display: block;
  color: @menu_text_color; 
  text-decoration: none; 
  font-weight: 300;
}
#cssmenu > ul {
  float: none;
}


#cssmenu ul {
  background: #fff;
}
#cssmenu > ul > li {
  border-left: 3px solid @menu_border_color;
}
#cssmenu > ul > li > a {
    padding: 10px 20px;
}

#cssmenu > ul > li:hover {
  border-left: 3px solid @main_color;
}
#cssmenu ul li:hover > a {
  color: @main_color;
}
#cssmenu > ul > li:hover {
  background: #f6f6f6;
}

/* Sub Menu */
#cssmenu ul ul a:link,
#cssmenu ul ul a:visited	{font-weight: 400; font-size: @sub_menu_font_size;}

#cssmenu ul ul {
  width: @sub_menu_width;
  background: none;
  border-left: 20px solid transparent;
}
#cssmenu ul ul a {
  padding: 8px 0;
  border-bottom: 1px solid #eeeeee;
}
#cssmenu ul ul li {
  padding: 0 20px;
  background: #fff;
}
#cssmenu ul ul li:last-child {
  border-bottom: 3px solid @menu_border_color;  
  padding-bottom: 10px;
}
#cssmenu ul ul li:first-child {
  padding-top: 10px;
}

#cssmenu ul ul li:last-child > a {
  border-bottom: none;  
}

#cssmenu ul ul li:first-child:after {
  content: '';
  display: block;
  width: 0;
  height: 0;
  position: absolute;
  left: -20px;
  top: 13px;
  border-left: 10px solid transparent;
  border-right: 10px solid #fff;
  border-bottom: 10px solid transparent;
  border-top: 10px solid transparent;  
}
