css:
@charset "UTF-8";

@main_color : [[main_color:#a7bc7a]];
@menu_width : [[menu_width:auto]];
@menu_font_size : [[menu_font_size:14px]];
@menu_text_color : [[menu_text_color:#fff]];

// Less Styles
@light-color: lighten(@main_color, 10%);
@dark-color: darken(@main_color, 10%);
@dark-color2: darken(@main_color, 15%);
@dark-color3: darken(@main_color, 30%);

.rounded-corners (@radius: 5px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  border-radius: @radius;
}

.rounded-corner (@1, @2, @3, @4) {
  -webkit-border-radius: @1 @2 @3 @4;
  -moz-border-radius: @1 @2 @3 @4;
  border-radius: @1 @2 @3 @4;
}

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background:-moz-linear-gradient(@top-color 0%, @bottom-color 100%); 
	background:-webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background:-webkit-linear-gradient(@top-color 0%,@bottom-color 100%);  
	background:linear-gradient(@top-color 0%,@bottom-color 100%);
}

.box-shadow(@x, @y, @spread, @color) {
	-moz-box-shadow: @x @y @spread @color;
	-webkit-box-shadow:@x @y @spread @color;
	box-shadow: @x @y @spread @color;
}

.box-shadow-inset(@x, @y, @spread, @color) {
	-moz-box-shadow: inset @x @y @spread @color;
	-webkit-box-shadow: inset @x @y @spread @color;
	box-shadow: inset @x @y @spread @color;
}
.text-shadow(@x, @y, @spread, @color) { text-shadow: @x @y @spread @color; }

.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}


/* Starter CSS for Flyout Menu */
#cssmenu,
#cssmenu ul,
#cssmenu li
#cssmenu a {
  list-style: none; 
  margin: 0; 
  padding: 0;
  border: 0;
  font-size: @menu_font_size;
  font-family: Helvetica;
  line-height: 1;
}

#cssmenu {
  width: @menu_width;
}
#cssmenu ul {
  .clearfix();
  background: @main_color url(#include_path#pattern.png) top left repeat;
  .rounded-corners(4px);
  border: 1px solid darken(@main_color, 15%);
  .box-shadow(0, 3px, 3px, rgba(0,0,0,.3));
}

#cssmenu a,
#cssmenu a:link,
#cssmenu a:visited {
  padding: 15px 20px;
  display: block;
  text-decoration: none;
  color: @menu_text_color;
  .text-shadow(0,-1px, 1px, @dark-color3);
  border-right: 1px solid darken(@main_color, 15%);    
}
#cssmenu a:hover {
  color: @dark-color3;
  .text-shadow(0, 1px, 1px, @light-color);  
}


#cssmenu li {
  float: left;
  border-right: 1px solid lighten(@main_color, 5%);  
}
#cssmenu li:hover {
  background: darken(@main_color,5%) url(#include_path#pattern.png) top left repeat;   
}
#cssmenu li:first-child{
  border-left: none;
  .rounded-corner(4px, 0, 0, 4px);
}
