css:
@import url(http://fonts.googleapis.com/css?family=Open+Sans:400,600,300);

// Colours
@themeColour: #d80041;
@main_color :  [[main_color:#d80041]];
@navBackground: [[menu_background_color:#fff]];
@borderColour: [[menu_border_color:#ece6e8]];
@shadowColour: #d9ced2;
@fontColour: [[menu_text_color:#747474]];

// Layout
@navWidth: [[menu_width:auto]];
@subNavWidth: [[sub_menu_width:250px]];
@baseLine: 12px;
@fontSize: 10px;
@responsiveCollapse: 600px;

//
// Mixins
//

// Border Radius
.rounded(@radius: 2px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  -o-border-radius: @radius;
  border-radius: @radius;
}


#cssmenu{
	font-family:"Open Sans", "Helvetica Neue", Helvetica, Arial, Sans-Serif;
	font-size:@fontSize;
	line-height:@fontSize * 1.5;
	text-transform:uppercase;
	text-align:left;

	 > ul{
		width:@navWidth;
		list-style-type:none;
		padding:0;
		margin:0;
		background:@navBackground;
		border:1px solid @borderColour;
		border-bottom:3px solid @shadowColour;
		.rounded;

		li#responsive-tab{
			display:none; /* Hide for large screens */
		}

		li{
			display: inline-block;
			*display: inline;
			zoom: 1;

			&.right{
				float:right;
			}

			&.has-sub{
				position:relative;

				&:hover ul{
					display:block;
				}

				ul{
					display:none;
					width:@subNavWidth;
					position:absolute;
					margin:0;
					padding:0;
					list-style-type:none;
					background:@navBackground;
					border:1px solid @borderColour;
					border-bottom:3px solid @shadowColour;
					border-top:0 none;

					li{
						display:block;
					}
				}

				> a{
					background-image:url('#include_path#caret.png');
					background-repeat:no-repeat;
					background-position: 90% -95%;

					&.active, &:hover{
						background:@main_color url('#include_path#caret.png') no-repeat;
						background-position: 90% 195%;						
					}

				}
			}

			a{
				display:block;
				padding:@baseLine @baseLine * 2 @baseLine - 1 @baseLine * 2;
				text-decoration:none;
				color:@fontColour;
				text-shadow: 0px 1px 0px #fff;

				&.active, &:hover{
					background: @main_color;
					color:#fff;
					text-shadow: 0px 1px 0px #000;
				}
			}
		}

	}

}

@media (max-width: @responsiveCollapse){

#cssmenu{

	> ul{
		width:100%;

		li#responsive-tab{
			display:block;

			a{
				background:url('#include_path#menu.png') no-repeat;
				background-position:95% -35%;

				&:hover{
					background-color:@main_color;
					background-position:95% 135%;
				}
			}
		}

		li{
			display:none;

			&.right{
				float:none;
			}

			&.has-sub{
				position:relative;

				ul{
					display:block;
					position:static;
					width:100%;
					background:@navBackground;
					border:0 none;

					li{
						display:block !important;

						a{
							span{
								display:block;
								padding-left:@baseLine * 2;
							}
						}
					}
				}

				> a{
					background-image:none;
				}
			}
		}
	}
}
}

/* Make sure they show even if hidden in mobile view by JS */
@media (min-width: @responsiveCollapse){
	#cssmenu{
	 > ul{
		> li{
			&.collapsed{
				display:inline-block !important; 
				*display: inline;
				zoom: 1;
				}
			}

			ul li.collapsed{
				display:block !important; 
			}
		}
	}
}

jquery:
// Cache the elements we'll need
var menu = $('#cssmenu');
var menuList = menu.find('ul:first');
var listItems = menu.find('li').not('#responsive-tab');

// Create responsive trigger
menuList.prepend('<li id="responsive-tab"><a href="#">Menu</a></li>');

// Toggle menu visibility
menu.on('click', '#responsive-tab', function(){
	listItems.slideToggle('fast');
	listItems.addClass('collapsed');
});
