css:
@charset "UTF-8";
@import url(http://fonts.googleapis.com/css?family=Open+Sans:400,600,300);

@main_color :  #79980d;
@menu_width : 100%;
@menu_font_size : 12px;
@menu_text_color : #fff;
@menu_text_hover_color : #000;
@menu_background_color : #565656;
@menu_border_color : #000;
@sub_menu_width : 200px;
@sub_menu_font_size : 12px;
@sub_menu_text_color : #999;
@sub_menu_text_hover_color : #444;
@sub_menu_background_color : #F9F9F9;
@sub_menu_border_color : #CCC;

// Less Styles
@light-color: lighten(@main_color, 8%);
@dark-color: darken(@main_color, 10%);
@dark-color1: darken(@main_color, 20%);
@dark-color2: darken(@main_color, 30%);
@dark-color3: darken(@main_color, 40%);

.rounded-corners (@radius: 5px) {
  -webkit-border-radius: @radius;
  -moz-border-radius: @radius;
  border-radius: @radius;
}

.rounded-corner (@1, @2, @3, @4) {
  -webkit-border-radius: @1 @2 @3 @4;
  -moz-border-radius: @1 @2 @3 @4;
  border-radius: @1 @2 @3 @4;
}

.linear-gradient (@top-color, @bottom-color) {
	background: @top-color; 
	background: -moz-linear-gradient(@top-color 0%, @bottom-color 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,@top-color), color-stop(100%,@bottom-color)); 
	background: -webkit-linear-gradient(@top-color 0%,@bottom-color 100%);  
	background: linear-gradient(@top-color 0%, @bottom-color 100%); 
}

.box-shadow(@x, @y, @spread, @color) {
	-moz-box-shadow: @x @y @spread @color;
	-webkit-box-shadow:@x @y @spread @color;
	box-shadow: @x @y @spread @color;
}

.box-shadow-inset(@x, @y, @spread, @color) {
	-moz-box-shadow: inset @x @y @spread @color;
	-webkit-box-shadow: inset @x @y @spread @color;
	box-shadow: inset @x @y @spread @color;
}
.text-shadow(@x, @y, @spread, @color) { text-shadow: @x @y @spread @color; }

.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}


/* Base Styles */
#menu_class#,
#menu_class# ul,
#menu_class# li,
#menu_class# a {
	margin: 0; 
	padding: 0; 
	border: 0; 
	list-style: none; 
	font-weight: normal;
	text-decoration: none;
	line-height: 1; 
	font-family: 'Open Sans', sans-serif;
	font-size: 14px;
	position: relative;
}
#menu_class# a {
	line-height: 1.5;
}

#menu_class# {
	width: @menu_width;
	background: #fff;
	
	> ul { 
		border: 1px solid @menu_border_color;
		.box-shadow-inset(1px, 1px, 0, rgba(255,255,255,0.2));
		.linear-gradient(@menu_background_color, darken(@menu_background_color,14%) ); 
		.rounded-corners(4px);

		&:after {
			clear: both;
			content: '';
			display: table;
		}
	}
}
@media all and (max-width: 480px) {
	#menu_class# > ul { 
		max-height: 32px; 
		overflow: hidden;
		-webkit-transition: max-height 0.5s;
		transition: max-height 0.5s; 
	}

	#menu_class#.expand ul { max-height: 900px; }
}

#menu_class# > ul > li {   
	border-right: 1px solid @menu_border_color;
	.box-shadow(1px, 0, 0, rgba(255,255,255,0.2));
	float: left;

	&.mobile { display: none; }
}

#menu_class# > ul > li.active {
	.linear-gradient(darken(@menu_background_color,7%), darken(@menu_background_color,14%));
}   

@media all and (max-width: 480px) {
	#menu_class# > ul > li { 
		border-right: 0 none;
		.box-shadow(0, 0, 0, transparent);
		display: block;
		float: none;

		&.mobile { 
			display: block; 
			i { float: right; font-style: normal; }
		}
	}
}

#menu_class# > ul > li:last-child {
	border-right: 0 none;
	.box-shadow(0, 0, 0, transparent);
	.rounded-corners( 0 4px 4px 0);
	ul { left: auto; right: 0; }
}
#menu_class# > ul > li.mobile {
	border-right: 0 none;
	.box-shadow(0, 0, 0, transparent);
}
#menu_class# > ul > li > a  {
	margin: 0 0 2px 0;
	font-size: @menu_font_size;
	display: block;
	color: @menu_text_color; 
	text-shadow: 0 1px 1px #000;
}
#menu_class# > ul > li > a > span {
	display: block;
	padding: 6px 10px;
	font-weight: bold;
}
#menu_class# > ul > li > a:hover {
	text-decoration: none;
}

#menu_class# > ul > li.active {
	border-bottom: none;
}
#menu_class# > ul > li.active > a {
	color: #fff;
	text-shadow: 0 1px 1px #000;
	
}
#menu_class# > ul > li.active > a span { }

/* Sub menu */
#menu_class# ul ul {
  background: @sub_menu_background_color;
  border: 1px solid @sub_menu_border_color;
  .rounded-corners(4px);
  .box-shadow(1px, 1px, 3px, rgba(0,0,0,0.25));
  padding: 5px 12px;
  display: none;
  position: absolute;
  top: 33px; left: 0;
  width: @sub_menu_width;
  z-index: 1;
}

@media all and (max-width: 480px) {
	#menu_class# ul ul {
		position: relative;
		top: 0;
		width: 100%;
	}
}

#menu_class# ul ul li {
	padding: 3px 0;
}
#menu_class# ul ul a {
	color: @sub_menu_text_color;
	display: block;
	font-size: @sub_menu_font_size;
	font-weight: bold;
}

#menu_class# ul ul a:hover {
	color: @sub_menu_text_hover_color;
}

jquery:
$('#cssmenu > ul').prepend('<li class="mobile"><a href="#"><span>Menu <i>&#9776;</i></span></a></li>');
$('#cssmenu > ul > li > a').click(function(e) {
  $('#cssmenu li').removeClass('active');
  $(this).closest('li').addClass('active');	
  var checkElement = $(this).next();
  if((checkElement.is('ul')) && (checkElement.is(':visible'))) {
    $(this).closest('li').removeClass('active');
    checkElement.slideUp('normal');
  }
  if((checkElement.is('ul')) && (!checkElement.is(':visible'))) {
    $('#cssmenu ul ul:visible').slideUp('normal');
    checkElement.slideDown('normal');
  }
  if( $(this).parent().hasClass('mobile') ) {
    e.preventDefault();
    $('#cssmenu').toggleClass('expand');
  }
  if($(this).closest('li').find('ul').children().length == 0) {
    return true;
  } else {
    return false;	
  }		
});
