css:
@main_color: [[main_color: #2b2f3a]];
@menu_text_color: [[menu_text_color: #7a8189]];
@menu_text_hover_color: [[menu_text_hover_color: #ffffff]];
@menu_width: [[menu_width: auto]];
@menu_font_size: [[menu_font_size: 14px]];

@sub_menu_width : [[sub_menu_width: 130px]];
@sub_menu_text_color : [[sub_menu_text_color: #9ea2a5]];
@sub_menu_text_hover_color : [[sub_menu_text_hover_color: #8c9195]];
@sub_menu_background_color: [[sub_menu_background_color: #ffffff]];
@sub_menu_border_color: [[sub_menu_border_color: #eeeeee]];
@sub_menu_font_size: [[sub_menu_font_size: 12px]];


@sub_menu_background_hover_color: darken(@sub_menu_background_color, 5%);

#cssmenu {
	position: relative;
	height: 44px;
	background: @main_color;
    width: @menu_width;
}

#cssmenu ul {
	list-style: none;
	padding: 0;
	margin: 0;
	line-height: 1;
}

#cssmenu>ul {
	position: relative;
	display: block;
	background: @main_color;
	width: 100%;
    z-index: 500;
}

#cssmenu:after, #cssmenu>ul:after {
  content: ".";
  display: block;
  clear: both;
  visibility: hidden;
  line-height: 0;
  height: 0;
}

#cssmenu.align-right>ul>li {
  float: right;
}

#cssmenu.align-center ul {
  text-align: center;
}

#cssmenu.align-center ul ul {
	text-align: left;
}

#cssmenu>ul>li {
	display: inline-block;
	position: relative;
	margin: 0;
	padding: 0;
}

#cssmenu>ul>#menu-button {
	display: none;
}

#cssmenu ul li a {
	display: block;
	font-family: Helvetica, sans-serif;
	text-decoration: none;
}

#cssmenu>ul>li>a {
	font-size: @menu_font_size;
	font-weight: bold;
	padding: 15px 20px;
	color: @menu_text_color;
	text-transform: uppercase;

	-webkit-transition: color .25s ease-out;
	-moz-transition: color .25s ease-out;
	-ms-transition: color .25s ease-out;
	-o-transition: color .25s ease-out;
	transition: color .25s ease-out;
}

#cssmenu>ul>li.has-sub>a {
	padding-right: 32px;
}

#cssmenu>ul>li:hover>a {
	color: @menu_text_hover_color;
}

#cssmenu li.has-sub::after {
	display: block;
	content: "";
	position: absolute;
	width: 0;
	height: 0;
}

#cssmenu>ul>li.has-sub::after {
	right: 10px;
	top: 15 + (@menu_font_size - 4)/2;
	border: 5px solid transparent;
	border-top-color: @menu_text_color;
}

#cssmenu>ul>li:hover::after {
	border-top-color: @menu_text_hover_color;
}

#indicatorContainer {
	position: absolute;
	height: 12px;
	width: 100%;
	bottom: 0px;
	overflow: hidden;
	z-index: -1;
}

#pIndicator {
	position: absolute;
	height: 0;
	width: 100%;
	border: 12px solid transparent;
	border-top-color: #2b2f3a;
	z-index: -2;
	-webkit-transition: left .25s ease;
	-moz-transition: left .25s ease;
	-ms-transition: left .25s ease;
	-o-transition: left .25s ease;
	transition: left .25s ease;
}

#cIndicator {
	position: absolute;
	height: 0;
	width: 100%;
	border: 12px solid transparent;
	border-top-color: #2b2f3a;
	top: -12px;
	right: 100%;
	z-index: -2;
}

#cssmenu ul ul {
	position: absolute;
	left: -9999px;
	top: 56 + @menu_font_size;
	opacity: 0;
	-webkit-transition: opacity .3s ease, top .25s ease;
	-moz-transition: opacity .3s ease, top .25s ease;
	-ms-transition: opacity .3s ease, top .25s ease;
	-o-transition: opacity .3s ease, top .25s ease;
	transition: opacity .3s ease, top .25s ease;
    z-index: 1000;
}

#cssmenu ul ul ul {
	top: 37px;
	padding-left: 5px;
}

#cssmenu ul ul li {
	position: relative;
}

#cssmenu>ul>li:hover>ul {
	left: auto;
	top: 30 + @menu_font_size;
	opacity: 1;
}

#cssmenu.align-right>ul>li:hover>ul {
	left: auto;
	right: 0;
	opacity: 1;
}

#cssmenu ul ul li:hover>ul {
	left: (@sub_menu_width + 40);
	top: 0;
	opacity: 1;
}

#cssmenu.align-right ul ul li:hover>ul {
	left: auto;
	right: (@sub_menu_width + 40);
	top: 0;
	opacity: 1;
	padding-right: 5px;
}

#cssmenu ul ul li a {
	width: @sub_menu_width;
	border-bottom: 1px solid @sub_menu_border_color;
	padding: 10px 20px;
	font-size: @sub_menu_font_size;
	color: @sub_menu_text_color;
	background: @sub_menu_background_color;
	-webkit-transition: all .35s ease;
	-moz-transition: all .35s ease;
	-ms-transition: all .35s ease;
	-o-transition: all .35s ease;
	transition: all .35s ease;
}

#cssmenu.align-right ul ul li a {
	text-align: right;
}

#cssmenu ul ul li:hover>a {
	background: @sub_menu_background_hover_color;
	color: @sub_menu_text_hover_color;
}

#cssmenu ul ul li:last-child>a, #cssmenu ul ul li.last>a {
	border-bottom: 0;
}

#cssmenu>ul>li>ul::after {
	content: '';
	border: 6px solid transparent;
	width: 0;
	height: 0;
	border-bottom-color: @sub_menu_background_color;
	position: absolute;
	top: -12px;
	left: 30px;
}

#cssmenu.align-right>ul>li>ul::after {
	left: auto;
	right: 30px;
}

#cssmenu ul ul li.has-sub::after {
	border: 4px solid transparent;
	border-left-color: @sub_menu_text_color;
	right: 10px;
	top: 10 + @sub_menu_font_size/2 - 4;
	-moz-transition: all .2s ease;
	-ms-transition: all .2s ease;
	-o-transition: all .2s ease;
	transition: all .2s ease;
	-webkit-transition: -webkit-transform .2s ease, right .2s ease;
}

#cssmenu.align-right ul ul li.has-sub::after {
	border-left-color: transparent;
	border-right-color: @sub_menu_text_color;
	right: auto;
	left: 10px;
}

#cssmenu ul ul li.has-sub:hover::after {
	border-left-color: @sub_menu_background_color;
	right: -5px;
	-webkit-transform: rotateY(180deg);
	-ms-transform: rotateY(180deg);
	-moz-transform: rotateY(180deg);
	-o-transform: rotateY(180deg);
	transform: rotateY(180deg);
}

#cssmenu.align-right ul ul li.has-sub:hover::after {
	border-right-color: @sub_menu_background_color;
	border-left-color: transparent;
	left: -5px;
	-webkit-transform: rotateY(180deg);
	-ms-transform: rotateY(180deg);
	-moz-transform: rotateY(180deg);
	-o-transform: rotateY(180deg);
	transform: rotateY(180deg);
}

@media all and (max-width: 800px), 
only screen and (-webkit-min-device-pixel-ratio: 2)      and (max-width: 1024px),
only screen and (   min--moz-device-pixel-ratio: 2)      and (max-width: 1024px),
only screen and (     -o-min-device-pixel-ratio: 2/1)    and (max-width: 1024px),
only screen and (        min-device-pixel-ratio: 2)      and (max-width: 1024px),
only screen and (                min-resolution: 192dpi) and (max-width: 1024px),
only screen and (                min-resolution: 2dppx)  and (max-width: 1024px) {
    #cssmenu {
    	width: auto;
    }

    #cssmenu.align-center ul {
		text-align: left;
    }

    #cssmenu.align-right>ul>li {
		float: none;
    }

	#cssmenu ul {
		width: auto;
	}


	#cssmenu .submenuArrow, #cssmenu #indicatorContainer {
		display: none;
	}
	
	#cssmenu>ul {
		height: auto;
		display: block;
	}

	#cssmenu>ul>li {
		float: none;
	}

	#cssmenu li, #cssmenu>ul>li {
		display: none;
	}

	#cssmenu ul ul, 
	#cssmenu ul ul ul, 
	#cssmenu ul>li:hover>ul, 
	#cssmenu ul ul>li:hover>ul,
	#cssmenu.align-right ul ul, 
	#cssmenu.align-right ul ul ul, 
	#cssmenu.align-right ul>li:hover>ul, 
	#cssmenu.align-right ul ul>li:hover>ul {
		position: relative;
		left: auto;
		top: auto;
		opacity: 1;
		padding-left: 0;
		padding-right: 0;
		right: auto;
	}

	#cssmenu ul .has-sub::after {
		display: none;
	}

	#cssmenu ul li a {
		padding: 12px 20px;
	}

	#cssmenu ul ul li a {
		border: 0;
		background: none;
		width: auto;
		padding: 8px 35px;
	}

	#cssmenu.align-right ul ul li a {
		text-align: left;
	}

	#cssmenu ul ul li:hover>a {
		background: none;
		color: @sub_menu_text_hover_color;
	}

	#cssmenu ul ul ul a {
		padding: 8px 50px;
	}

	#cssmenu ul ul ul ul a {
		padding: 8px 65px;
	}

	#cssmenu ul ul ul ul ul a {
		padding: 8px 80px;
	}

	#cssmenu ul ul ul ul ul ul a {
		padding: 8px 95px;
	}

	#cssmenu>ul>#menu-button {
		display: block;
		cursor: pointer;
	}

	#cssmenu #menu-button>a {
		padding: 14px 20px;
	}

	#cssmenu ul.open li, #cssmenu>ul.open>li {
		display: block;
	}

	#cssmenu>ul.open>li#menu-button>a {
		color: #fff;
		border-bottom: 1px solid rgba(150, 150, 150, .1)
	}

	#cssmenu ul ul::after {
		display: none;
	}

	#cssmenu #menu-button::after {
		display: block;
		content: '';
		position: absolute;
		height: 3px;
		width: 22px;
		border-top: 2px solid @menu_text_color;
		border-bottom: 2px solid @menu_text_color;
		right: 20px;
		top: 15px;
	}

	#cssmenu #menu-button::before {
		display: block;
		content: '';
		position: absolute;
		height: 3px;
		width: 22px;
		border-top: 2px solid @menu_text_color;
		right: 20px;
		top: 25px;
	}

	#cssmenu ul.open #menu-button::after, #cssmenu ul.open #menu-button::before {
		border-color: #fff;
	}
}

