css:
@main_color:  [[main_color:  #222222]];
@menu_width: [[menu_width: 250px]];
@menu_font_size: [[menu_font_size: 16px]];
@menu_text_color : [[menu_text_color : #dddddd]];
@menu_text_hover_color:[[menu_text_hover_color: #FFFFFF]];
@menu_border_color : [[menu_border_color : #000000]];
@sub_menu_font_size : [[sub_menu_font_size : 15px]];
@sub_menu_text_color : [[sub_menu_text_color : #777777]];
@sub_menu_text_hover_color : [[sub_menu_text_hover_color : #666666]];
@sub_menu_background_color : [[sub_menu_background_color : #f1f1f1]];
@sub_menu_border_color : [[sub_menu_border_color : #dddddd]];

@menu_hover: darken(@main_color, 5%);
@submenu_hover: darken(@sub_menu_background_color, 5%);

@import url(http://fonts.googleapis.com/css?family=Roboto+Condensed:400,300);

#cssmenu {
	position: relative;
	margin: 0;
	font-family: 'Roboto Condensed';
	line-height: 1;
	width: @menu_width;
}

.align-right {
	float: right;
}

#cssmenu ul {
	margin: 0;
	padding: 0;
	list-style: none;
	display: block;
}

#cssmenu ul li {
	position: relative;
	margin: 0;
	padding: 0;
}

#cssmenu ul li a {
	text-decoration: none;
	cursor: pointer;
}

#cssmenu>ul>li>a {
	color: @menu_text_color;
	text-transform: uppercase;
	display: block;
	padding: 20px;
	border-top: 1px solid @menu_border_color;
	border-left: 1px solid @menu_border_color;
	border-right: 1px solid @menu_border_color;
	background: @main_color;
	box-shadow: inset 0 1px 0 rgba(255, 255, 255, .1);
	letter-spacing: 1px;
	font-size: @menu_font_size;
	font-weight: 300;
	-webkit-transition: all .25s ease-in;
	-moz-transition: all .25s ease-in;
	-ms-transition: all .25s ease-in;
	-o-transition: all .25s ease-in;
	transition: all .25s ease-in;
	position: relative;

}

#cssmenu>ul>li:first-child>a {
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
}

#cssmenu>ul>li:last-child>a {
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
	border-bottom: 1px solid @menu_border_color;
}

#cssmenu>ul>li:hover>a, #cssmenu>ul>li.open>a, #cssmenu>ul>li.active>a {
	background: @menu_hover;
	color: @menu_text_hover_color;
}

#cssmenu ul>li.has-sub>a::after {
	content: "";
	position: absolute;
	display: block;
	width: 0;
	height: 0;
	border-top: 13px solid @menu_border_color;
	border-botom: 13px solid transparent;
	border-left: (@menu_width/2) solid transparent;
	border-right: (@menu_width/2) solid transparent;
	left: 0;
	bottom: -13px;
	bottom: 0px;
	z-index: 1;
	opacity: 0;
	-webkit-transition: all .2s ease;
	-moz-transition: all .2s ease;
	-ms-transition: all .2s ease;
	-o-transition: all .2s ease;
	transition: all .2s ease;
}

#cssmenu ul>li.has-sub>a::before {
	content: "";
	position: absolute;
	display: block;
	width: 0;
	height: 0;
	border-top: 13px solid @menu_hover;
	border-botom: 13px solid transparent;
	border-left: (@menu_width/2) solid transparent;
	border-right: (@menu_width/2) solid transparent;
	left: 0;
	bottom: -12px;
	bottom: -1px;
	z-index: 3;
	opacity: 0;
	-webkit-transition: all .2s ease;
	-moz-transition: all .2s ease;
	-ms-transition: all .2s ease;
	-o-transition: all .2s ease;
	transition: all .2s ease;
}

#cssmenu ul>li.has-sub::after {
	content: "";
	display: block;
	position: absolute;
	width: 0;
	height: 0;
	border: 7px solid transparent;
	border-top-color: @menu_text_color;
	z-index: 2;
	right: 20px;
	top: 20 + (@menu_font_size - 7)/2;
	pointer-events: none;
}

#cssmenu ul>li:hover::after, #cssmenu ul>li.active::after, #cssmenu ul>li.open::after {
	border-top-color: @menu_text_hover_color;
}

#cssmenu ul>li.has-sub.open>a::after {
	opacity: 1;
	bottom: -13px;
}

#cssmenu ul>li.has-sub.open>a::before {
	opacity: 1;
	bottom: -12px;
}

#cssmenu ul ul {
	display: none;
}

#cssmenu ul ul li {
	border-left: 1px solid #ccc;
	border-right: 1px solid #ccc;
}

#cssmenu ul ul li a {
	background: @sub_menu_background_color;
	display: block;
	position: relative;
	font-size: @sub_menu_font_size;
	padding: 14px 20px;
	border-bottom: 1px solid @sub_menu_border_color;
	color: @sub_menu_text_color;
	font-weight: 300;
	-webkit-transition: all .25s ease-in;
	-moz-transition: all .25s ease-in;
	-ms-transition: all .25s ease-in;
	-o-transition: all .25s ease-in;
	transition: all .25s ease-in;
}

#cssmenu ul ul li:first-child>a {
	padding-top: 18px;
}

#cssmenu ul ul ul li {
	border: 0;
}

#cssmenu ul ul li:hover>a, #cssmenu ul ul li.open>a, #cssmenu ul ul li.active>a {
	background: @submenu_hover;
	color: @sub_menu_text_hover_color;
}

#cssmenu ul ul>li.has-sub>a::after {
	border-top: 13px solid @sub_menu_border_color;
}

#cssmenu ul ul>li.has-sub>a::before {
	border-top: 13px solid @submenu_hover;
}

#cssmenu ul ul ul li a {
	padding-left: 30px;
}

#cssmenu ul ul>li.has-sub::after {
	top: 14 + (@sub_menu_font_size - 6)/2;
	border-width: 6px;
	border-top-color: @sub_menu_text_color;
}

#cssmenu ul ul>li:hover::after, #cssmenu ul ul>li.active::after, #cssmenu ul ul>li.open::after {
	border-top-color: @sub_menu_text_hover_color;
}

jquery:
$('#cssmenu li.has-sub>a').on('click', function(){
		$(this).removeAttr('href');
		var element = $(this).parent('li');
		if (element.hasClass('open')) {
			element.removeClass('open');
			element.find('li').removeClass('open');
			element.find('ul').slideUp();
		}
		else {
			element.addClass('open');
			element.children('ul').slideDown();
			element.siblings('li').children('ul').slideUp();
			element.siblings('li').removeClass('open');
			element.siblings('li').find('li').removeClass('open');
			element.siblings('li').find('ul').slideUp();
		}
	});
