css:
@main_color:  [[main_color:  #1275ae]];
@menu_width: [[menu_width: auto]];
@menu_background_color: [[menu_background_color: #222222]];
@menu_font_size: [[menu_font_size: 12px]];
@menu_text_color : [[menu_text_color : #ffffff]];
@menu_text_hover_color :[[menu_text_hover_color : #ffffff]];


@color_stop1: darken(@menu_background_color, 15%);
@color_stop2: @menu_background_color;
@color_stop3: lighten(@menu_background_color, 10%);
@color_stop4: lighten(@menu_background_color, 9%);
@color_stop5: lighten(@menu_background_color, 40%);

@color_stop6: darken(@main_color, 15%);
@color_stop7: @main_color;
@color_stop8: lighten(@main_color, 10%);
@color_stop9: lighten(@main_color, 9%);
@color_stop10: lighten(@main_color, 40%);


#cssmenu, #cssmenu ul, #cssmenu ul li, #cssmenu ul li a {
	margin: 0;
	padding: 0;
	border: 0;
	list-style: none;
	line-height: 1;
	display: block;
	position: relative;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;   
	box-sizing: border-box;
}

#cssmenu {
	width: @menu_width;
	font-family: Helvetica, Arial, sans-serif;
	color: @menu_text_color;
}

#cssmenu.align-right ul li {
	float: right;
	border-right: 0;
	border-left: 1px solid rgba(0, 0, 0, .22);
}

#cssmenu.align-right ul li a {
	border-right: 0;
	border-left: 1px solid rgba(255, 255, 255, .15);
}

#cssmenu ul {
	background: @menu_background_color; /* Old browsers */
	background: -moz-linear-gradient(bottom, @color_stop1 0%, @color_stop2 50%, @color_stop3 51%, @color_stop4 78%, @color_stop5 100%); 
	background: -webkit-linear-gradient(bottom, @color_stop1 0%,@color_stop2 50%,@color_stop3 51%, @color_stop4 78%, @color_stop5 100%); 
	background: -o-linear-gradient(bottom, @color_stop1 0%, @color_stop2 50%,@color_stop3 51%, @color_stop4 78%, @color_stop5 100%); 
	background: -ms-linear-gradient(bottom, @color_stop1 0%,@color_stop2 50%,@color_stop3 51%, @color_stop4 78%, @color_stop5 100%); 
	background: linear-gradient(to top, @color_stop1 0%,@color_stop2 50%,@color_stop3 51%, @color_stop4 78%, @color_stop5 100%); 
}

#cssmenu:after, #cssmenu ul:after {
	content: ".";
	display: block;
	clear: both;
	visibility: hidden;
	line-height: 0;
	height: 0;
}

#cssmenu ul li {
	float: left;
	display: block;
	border-right: 1px solid rgba(0, 0, 0, .22);
	z-index: 1;
}

#cssmenu ul li::after {
	content: "";
	width: 100%;
	height: 8px;
	position: absolute;
	border-top-left-radius: 50% 4px;
	border-top-right-radius: 50% 4px;
	background: -webkit-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop2 61%, @color_stop2 100%);
	background: -o-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop2 61%, @color_stop2 100%);
	background: -ms-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop2 61%, @color_stop2 100%);
	background: linear-gradient(to top, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop2 61%, @color_stop2 100%);
	background: -moz-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0));
	z-index: 2;
	bottom: 10px;
	bottom: 10 + (@menu_font_size - 12)/2;
}

#cssmenu ul li a {
	display: block;
	padding: 10px 30px;
	text-decoration: none;
	font-size: @menu_font_size;
	text-transform: uppercase;
	color: @menu_text_color;
	border-right: 1px solid rgba(255, 255, 255, .15);
	z-index: 3;
}

#cssmenu ul li a:hover, #cssmenu ul li.active a {
	color: @menu_text_hover_color;
}

#cssmenu ul li:hover, #cssmenu ul li.active {
	background: @main_color; /* Old browsers */
	background: -moz-linear-gradient(bottom, @color_stop6 0%, @color_stop7 50%, @color_stop8 51%, @color_stop9 78%, @color_stop10 100%); 
	background: -webkit-linear-gradient(bottom, @color_stop6 0%,@color_stop7 50%,@color_stop8 51%, @color_stop9 78%, @color_stop10 100%); 
	background: -o-linear-gradient(bottom, @color_stop6 0%, @color_stop7 50%,@color_stop8 51%, @color_stop9 78%, @color_stop10 100%);
	background: -ms-linear-gradient(bottom, @color_stop6 0%,@color_stop7 50%,@color_stop8 51%, @color_stop9 78%, @color_stop10 100%); 
	background: linear-gradient(to top, @color_stop6 0%,@color_stop7 50%,@color_stop8 51%, @color_stop9 78%, @color_stop10 100%); 
}


#cssmenu ul li:hover::after, #cssmenu ul li.active::after {
	background: -webkit-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop7 61%, @color_stop7 100%);
	
	background: -o-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop7 61%, @color_stop7 100%);
	background: -ms-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop7 61%, @color_stop7 100%);
	background: linear-gradient(to top, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0) 60%, @color_stop7 61%, @color_stop7 100%);
	background: -moz-linear-gradient(bottom, rgba(0, 0, 0, 0), rgba(0 , 0, 0, 0));
}
