css:
@main_color:  [[main_color:  #0f71ba]];
@menu_width: [[menu_width: auto]];
@menu_background_color: [[menu_background_color: #222222]];
@menu_font_size: [[menu_font_size: 15px]];
@menu_text_color : [[menu_text_color : #eeeeee]];
@menu_text_hover_color :[[menu_text_hover_color : #ffffff]];

@color_stop1: lighten(@menu_background_color, 20%);
@color_stop2: lighten(@main_color, 20%);

@import url(http://fonts.googleapis.com/css?family=Capriola);

#cssmenu, #cssmenu ul, #cssmenu ul li, #cssmenu ul li a {
  margin: 0;
  padding: 0;
  border: 0;
  list-style: none;
  line-height: 1;
  display: block;
  position: relative;
  font-family: Capriola, Helvetica, sans-serif;
}

#cssmenu {
  width: @menu_width;
  height: @menu_font_size + 44;
  padding-bottom: 4px;
}

#cssmenu.align-right {
  float: right;
}

#cssmenu.align-right ul li {
  float: right;
  margin-right: 0;
  margin-left: 4px;
}

#cssmenu.align-right ul li:first-child,
#cssmenu.align-right ul li:first-child>a {
  border-bottom-right-radius: 3px;
}

#cssmenu #bg-one,
#cssmenu #bg-two,
#cssmenu #bg-three,
#cssmenu #bg-four {
  position: absolute;
  bottom: 0;
  width: 100%;
  border-bottom-left-radius: 3px;
  border-bottom-right-radius: 3px;
}

#cssmenu #bg-one {
  height: 10px;
  background: @main_color;
}

#cssmenu #bg-two {
  height: @menu_font_size + 44;
  z-index: 2;
  background: url('#include_path#bg.png');
}

#cssmenu #bg-three {
  bottom: 4px;
  height: @menu_font_size + 40;
  z-index: 3;

  background: @menu_background_color; 
  background: -moz-linear-gradient(top,  @color_stop1 0%, @menu_background_color 100%); 
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,@color_stop1), color-stop(100%,@menu_background_color)); 
  background: -webkit-linear-gradient(top,  @color_stop1 0%,@menu_background_color 100%); 
  background: -o-linear-gradient(top,  @color_stop1 0%,@menu_background_color 100%); 
  background: -ms-linear-gradient(top,  @color_stop1 0%,@menu_background_color 100%);
  background: linear-gradient(to bottom,  @color_stop1 0%,@menu_background_color 100%); 
}

#cssmenu #bg-four {
  bottom: 4px;
  height: @menu_font_size + 40;
  z-index: 4;
  background: url('#include_path#bg.png');
}

#cssmenu ul {
  height: @menu_font_size + 44;
}

#cssmenu ul li {
  float: left;
  margin-right: 4px;
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
  z-index: 5;
}

#cssmenu ul li a {
  padding: 24px 30px 20px 30px;
  margin-bottom: 4px;
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
  color: @menu_text_color;
  font-size: @menu_font_size;
  text-decoration: none;
}

#cssmenu ul li:first-child,
#cssmenu ul li:first-child>a {
  border-bottom-left-radius: 3px;
}

#cssmenu ul li:hover,
#cssmenu ul li.active {
  background: @main_color; 
  background: -moz-linear-gradient(top,  @color_stop2 0%, @main_color 100%); 
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,@color_stop2), color-stop(100%,@main_color)); 
  background: -webkit-linear-gradient(top,  @color_stop2 0%,@main_color 100%); 
  background: -o-linear-gradient(top,  @color_stop2 0%,@main_color 100%); 
  background: -ms-linear-gradient(top,  @color_stop2 0%,@main_color 100%);
  background: linear-gradient(to bottom,  @color_stop2 0%,@main_color 100%); 
}

#cssmenu ul li a:hover, 
#cssmenu ul li.active>a {
  background: url('#include_path#bg.png');
  color: @menu_text_hover_color;
}
