css:
@main_color:  [[main_color:  #39B1CC]];
@menu_width: [[menu_width: auto]];
@menu_font_size: [[menu_font_size: 13px]];
@menu_text_color : [[menu_text_color : #D3ECED]];
@menu_text_hover_color :[[menu_text_hover_color : #ffffff]];
@sub_menu_font_size : [[sub_menu_font_size : 13px]];
@sub_menu_text_color : [[sub_menu_text_color : #777777]];
@sub_menu_text_hover_color : [[sub_menu_text_hover_color : #333333]];
@sub_menu_background_color : [[sub_menu_background_color : #ffffff]];
@sub_menu_width: [[sub_menu_width: 180px]];

@color_start: darken(@main_color, 8%);
@color_end: lighten(@main_color, 6%);
@link_hover: darken(@main_color, 3%);

@import url(http://fonts.googleapis.com/css?family=Open+Sans:400,700);

#cssmenu, #cssmenu ul, #cssmenu ul li, #cssmenu ul li a, #cssmenu #menu-button {
	margin: 0;
	padding: 0;
	border: 0;
	list-style: none;
	line-height: 1;
	display: block;
	position: relative;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;   
	box-sizing: border-box;
}

#cssmenu:after, #cssmenu>ul:after {
	content: ".";
	display: block;
	clear: both;
	visibility: hidden;
	line-height: 0;
	height: 0;
}

#cssmenu #menu-button {
	display: none;
}

#cssmenu {
	width: @menu_width;

	font-family: 'Open Sans', Helvetica, sans-serif;
	
	background: @main_color; 
	background: -moz-linear-gradient(top, @color_end 0%, @color_start 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,@color_end), color-stop(100%,@color_start)); 
	background: -webkit-linear-gradient(top, @color_end 0%,@color_start 100%); 
	background: -o-linear-gradient(top, @color_end 0%,@color_start 100%);
	background: -ms-linear-gradient(top, @color_end 0%,@color_start 100%); 
	background: linear-gradient(to bottom, @color_end 0%,@color_start 100%);
}

#cssmenu>ul {
	background: url('#include_path#bg.png');;
	box-shadow: inset 0 -3px 0 rgba(0, 0, 0, .05);
}

#cssmenu.align-right>ul>li {
	float: right;
}

#cssmenu>ul>li {
	float: left;
	display: inline-block;
}

#cssmenu.align-center>ul {
	float: none;
	text-align: center;
}

#cssmenu.align-center>ul>li {
	float: none;
}

#cssmenu.align-center ul ul {
	text-align: left;
}

#cssmenu>ul>li>a {
	padding: 18px 25px 21px 25px;
	border-right: 1px solid rgba(80, 80, 80, .12);

	text-decoration: none;
	font-size: @menu_font_size;
	font-weight: 700;
	color: @menu_text_color;
	text-transform: uppercase;
	letter-spacing: 1px;
}

#cssmenu>ul>li:hover>a, #cssmenu>ul>li>a:hover, #cssmenu>ul>li.active>a {
	color: @menu_text_hover_color;
	background: @link_hover;
	background: rgba(0, 0, 0, .1);
}

#cssmenu>ul>li.has-sub>a {
	padding-right: 45px;
}

#cssmenu>ul>li.has-sub>a::after {
	content: "";
	position: absolute;
	width: 0;
	height: 0;
	border: 6px solid transparent;
	border-top-color: @menu_text_color;
	right: 17px;
	top: 18 + (@menu_font_size - 5)/2;
}

#cssmenu>ul>li.has-sub.active>a::after, #cssmenu>ul>li.has-sub:hover>a {
	border-top-color: @menu_text_hover_color;
}

#cssmenu ul ul {
	position: absolute;
	left: -9999px;
	top: 60px;
	
	padding-top: 6px;

	font-size: @sub_menu_font_size;
	opacity: 0;
	-webkit-transition: top .2s ease, opacity .2s ease-in;
	-moz-transition: top .2s ease, opacity .2s ease-in;
	-ms-transition: top .2s ease, opacity .2s ease-in;
	-o-transition: top .2s ease, opacity .2s ease-in;
	transition: top .2s ease, opacity .2s ease-in;
}

#cssmenu.align-right ul ul {
	text-align: right;
}

#cssmenu>ul>li>ul::after {
	content: "";
	position: absolute;
	width: 0;
	height: 0;
	border: 5px solid transparent;
	border-bottom-color: @sub_menu_background_color;
	top: -4px;
	left: 20px;
}

#cssmenu.align-right>ul>li>ul::after {
	left: auto;
	right: 20px;
}

#cssmenu ul ul ul::after {
	content: "";
	position: absolute;
	width: 0;
	height: 0;
	border: 5px solid transparent;
	border-right-color: @sub_menu_background_color;
	top: 11px;
	left: -4px;
}

#cssmenu.align-right ul ul ul::after {
	border-right-color: transparent;
	border-left-color: @sub_menu_background_color;
	left: auto;
	right: -4px;
}

#cssmenu>ul>li>ul {
	top: 120px;
}

#cssmenu>ul>li:hover>ul {
	top: @menu_font_size + 39;
	left: 0;
	opacity: 1;
}

#cssmenu.align-right>ul>li:hover>ul {
	left: auto;
	right: 0;
}

#cssmenu ul ul ul {
	padding-top: 0;
	padding-left: 6px;
}

#cssmenu.align-right ul ul ul {
	padding-right: 6px;
}

#cssmenu ul ul>li:hover>ul {
	left: @sub_menu_width;
	top: 0;
	opacity: 1;
}

#cssmenu.align-right ul ul>li:hover>ul {
	left: auto;
	right: 100%;
	opacity: 1;
}

#cssmenu ul ul li a {
	text-decoration: none;
	font-weight: 400;
	padding: 11px 25px;
	width: @sub_menu_width;
	color: @sub_menu_text_color;
	background: @sub_menu_background_color;
	box-shadow: 0 2px 2px rgba(0, 0, 0, .1),
				1px 1px 1px rgba(0, 0, 0, .1),
				-1px 1px 1px rgba(0, 0, 0, .1);
}

#cssmenu ul ul li:hover>a, #cssmenu ul ul li.active>a {
	color: @sub_menu_text_hover_color;
}

#cssmenu ul ul li:first-child>a {
	border-top-left-radius: 3px;
	border-top-right-radius: 3px;
}

#cssmenu ul ul li:last-child>a {
	border-bottom-left-radius: 3px;
	border-bottom-right-radius: 3px;
}

#cssmenu>ul>li>ul::after {
	position: absolute;
	display: block;
}

#cssmenu ul ul li.has-sub>a::after {
	content: "";
	position: absolute;
	width: 0;
	height: 0;
	border: 4px solid transparent;
	border-left-color: @sub_menu_text_color;
	right: 17px;
	top: 14px;
	top: 11 + (@sub_menu_font_size - 7)/2;
}

#cssmenu.align-right ul ul li.has-sub>a::after {
	border-left-color: transparent;
	border-right-color: @sub_menu_text_color;
	right: auto;
	left: 17px;
}

#cssmenu ul ul li.has-sub.active>a::after,
#cssmenu ul ul li.has-sub:hover>a::after {
	border-left-color: @sub_menu_text_hover_color;
	
}

#cssmenu.align-right ul ul li.has-sub.active>a::after,
#cssmenu.align-right ul ul li.has-sub:hover>a::after {
	border-right-color: @sub_menu_text_hover_color;
	border-left-color: transparent;
}

@media all and (max-width: 800px), 
only screen and (-webkit-min-device-pixel-ratio: 2)      and (max-width: 1024px),
only screen and (   min--moz-device-pixel-ratio: 2)      and (max-width: 1024px),
only screen and (     -o-min-device-pixel-ratio: 2/1)    and (max-width: 1024px),
only screen and (        min-device-pixel-ratio: 2)      and (max-width: 1024px),
only screen and (                min-resolution: 192dpi) and (max-width: 1024px),
only screen and (                min-resolution: 2dppx)  and (max-width: 1024px) {
	#cssmenu {
		background: @main_color;
	}

	#cssmenu>ul {
		display: none;
	}

	#cssmenu>ul.open {
		display: block;
		border-top: 1px solid rgba(0, 0, 0, .1);
	}

	#cssmenu.align-right>ul {
		float: none;
	}

	#cssmenu.align-center>ul {
		text-align: left;
	}

	#cssmenu>ul>li,
	#cssmenu.align-right>ul>li {
		float: none;
		display: block;
	}

	#cssmenu>ul>li>a {
		padding: 18px 25px 18px 25px;
		border-right: 0;
	}

	#cssmenu>ul>li:hover>a, #cssmenu>ul>li.active>a {
		background: rgba(0, 0, 0, .1);
	}

	#cssmenu #menu-button {
		display: block;
		text-decoration: none;
		font-size: 13px;
		font-weight: 700;
		color: @menu_text_color;
		padding: 18px 25px 18px 25px;
		text-transform: uppercase;
		letter-spacing: 1px;
		background: url('#include_path#bg.png');;
		cursor: pointer;
	}

	#cssmenu ul ul,
	#cssmenu ul li:hover>ul, 
	#cssmenu>ul>li>ul, 
	#cssmenu ul ul ul, 
	#cssmenu ul ul li:hover>ul,
	#cssmenu.align-right ul ul,
	#cssmenu.align-right ul li:hover>ul, 
	#cssmenu.align-right>ul>li>ul, 
	#cssmenu.align-right ul ul ul, 
	#cssmenu.align-right ul ul li:hover>ul
	  {
		left: 0;
		right: auto;
		top: auto;
		opacity: 1;
		width: 100%;
		padding: 0;
		position: relative;
		text-align: left;
	}

	#cssmenu ul ul li {
		width: 100%;
	}

	#cssmenu ul ul li a {
		width: 100%;
		box-shadow: none;
		padding-left: 35px;
	}

	#cssmenu ul ul ul li a {
		padding-left: 45px;
	}

	#cssmenu ul ul li:first-child>a, #cssmenu ul ul li:last-child>a {
		border-radius: 0;
	}

	#cssmenu #menu-button::after {
		display: block;
		content: '';
		position: absolute;
		height: 3px;
		width: 22px;
		border-top: 2px solid @menu_text_color;
		border-bottom: 2px solid @menu_text_color;
		right: 25px;
		top: 18px;
	}

	#cssmenu #menu-button::before {
		display: block;
		content: '';
		position: absolute;
		height: 3px;
		width: 22px;
		border-top: 2px solid @menu_text_color;
		right: 25px;
		top: 28px;
	}

	#cssmenu>ul>li.has-sub>a::after,
	#cssmenu ul ul li.has-sub>a::after {
		display: none;
	} 
}

jquery:
$('#cssmenu').prepend('<div id="menu-button">Menu</div>');
  $('#cssmenu #menu-button').on('click', function(){
    var menu = $(this).next('ul');
    if (menu.hasClass('open')) {
      menu.removeClass('open');
    } else {
      menu.addClass('open');
    }
});
