css:
@menu_background_color: [[menu_background_color: #dddddd]];
@menu_width: [[menu_width: auto]];
@menu_font_size: [[menu_font_size: 14px]];
@menu_text_color: [[menu_text_color: #0e83cd]];
@menu_text_hover_color: [[menu_text_hover_color: #333333]];
@menu_border_color: [[menu_border_color: #333333]];

#cssmenu, 
#cssmenu ul, 
#cssmenu ul li, 
#cssmenu ul li a {
  margin: 0;
  padding: 0;
  border: 0;
  list-style: none;
  line-height: 1;
  display: block;
  position: relative;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;   
  box-sizing: border-box;
}

#cssmenu {
  border-bottom: 3px solid @menu_border_color;
  padding: 0 5px;
  width: @menu_width;
  font-family: Helvetica, Arial, sans-serif;
  background: @menu_background_color;
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
}

#cssmenu:after, #cssmenu ul:after {
  content: ".";
  display: block;
  clear: both;
  visibility: hidden;
  line-height: 0;
  height: 0;
}

#cssmenu ul li {
  display: inline-block;
}

#cssmenu.align-right ul li {
  float: right;
}

#cssmenu.align-center ul {
  text-align: center;
}

#cssmenu ul li a {
  font-size: @menu_font_size;
  padding: 15px;
  text-decoration: none;
  color: @menu_text_color;
}

#cssmenu ul li:hover a,
#cssmenu ul li a:hover,
#cssmenu ul li.active a {
  color: @menu_text_hover_color;
}

@media all and (max-width: 640px) {
  #cssmenu {
    padding: 0;
    border-radius: 0;
    border: 0;
  }

  #cssmenu ul li, #cssmenu.align-center, #cssmenu.align-right {
    display: block;
  }

}
