css:
@main_color: [[main_color: #2580a2]];
@menu_font_size:[[menu_font_size:14px]];
@menu_width: [[menu_width: auto]];
@menu_text_color: [[menu_text_color:#cccccc]];
@sub_menu_text_color: [[sub_menu_text_color:#cccccc]];
@menu_text_hover_color: [[menu_text_hover_color:#FFFFFF]];
@sub_menu_width: [[sub_menu_width:225px]];
@sub_menu_font_size: [[sub_menu_font_size:12px]];
@sub_menu_text_hover_color: [[sub_menu_text_hover_color:#ffffff]];

#cssmenu{
	border:none;
	border:0px;
	margin:0px;
	padding:0px;
	font: 67.5% "Lucida Sans Unicode", "Bitstream Vera Sans", "Trebuchet Unicode MS", "Lucida Grande", Verdana, Helvetica, sans-serif;
	font-size: @menu_font_size;
	font-weight:bold;
    width: @menu_width;
}
#cssmenu ul
{
	background: #333333;
	height:35px;
	list-style:none;
	margin:0;
	padding:0;
	}
#cssmenu li{
	float:left;
	padding:0px;
	}
#cssmenu li a{
	background: #333333 url("#include_path#seperator.gif") bottom right no-repeat;
	display:block;
	font-weight:normal;
	line-height:35px;
	margin:0px;
	padding:0px 25px;
	text-align:center;
	text-decoration:none;
	}
#cssmenu > ul > li > a {color: @menu_text_color; }

#cssmenu ul ul a {color: @sub_menu_text_color;}

#cssmenu li > a:hover, #cssmenu ul li:hover > a {
	background: @main_color url("#include_path#hover.png") bottom center no-repeat;
	color: @menu_text_hover_color;
	text-decoration:none;
	}
#cssmenu li ul{
	background:#333333;
	display:none;
	height:auto;
	padding:0px;
	margin:0px;
	border:0px;
	position:absolute;
	width: @sub_menu_width;
	z-index:200;
	/*top:1em;
	/*left:0;*/
	}
#cssmenu li:hover ul{
	display:block;
	}
#cssmenu li li {
	background:url('#include_path#sub_sep.gif') bottom left no-repeat;
	display:block;
	float:none;
	margin:0px;
	padding:0px;
	width: @sub_menu_width;
	}
#cssmenu li:hover li a{
	background:none;
	
	}
#cssmenu li ul a{
	display:block;
	height:35px;
	font-size: @sub_menu_font_size;
	font-style:normal;
	margin:0px;
	padding:0px 10px 0px 15px;
	text-align:left;
	}
#cssmenu li ul a:hover, #cssmenu li ul li:hover > a{
	background: @main_color url('#include_path#hover_sub.png') center left no-repeat;
	border:0px;
	color: @sub_menu_text_hover_color;
	text-decoration:none;
	}
#cssmenu p{
	clear:left;
	}	

#cssmenu ul ul li {
	position: relative;
}

#cssmenu ul ul ul {
	left: -9999px;
	top: 0;
}

#cssmenu ul ul li:hover>ul {
	left: 100%;
}

#cssmenu.align-center>ul>li {
	display: inline-block;
	float: none;
}

#cssmenu.align-center>ul {
	text-align: center;
}

#cssmenu>ul>li {
	position: relative;
}

#cssmenu.align-right>ul>li {
	float: right;
}

#cssmenu.align-right>ul>li:hover>ul {
	right: 0;
	left: auto;
	text-align: right;
}

#cssmenu.align-right ul ul li a {
	text-align: right;
}

#cssmenu.align-right ul ul li:hover>ul {
	left: auto;
	right: 100%;
}
