css:
@main_color: [[main_color: #b9121b]];
@menu_width: [[menu_width: auto]];
@menu_text_color: [[menu_text_color: #ffffff]];

#menu_class# {
width: @menu_width;
	border:1px solid @main_color;
	background: @main_color;
	}

#menu_class# > ul {
	padding:1px 0;
	margin:0px;
	list-style:none;
	width:100%;
	height:21px;
	border-top:1px solid #FFFFFF;
	border-bottom:1px solid #FFFFFF;
	font:normal 8pt verdana, arial, helvetica;
}
#menu_class# > ul li {
	margin:0;
	padding:0;
	display:block;
	float:left;
	position:relative;
	width:148px;
}
#menu_class# > ul li a:link, #menu_class# > ul li a:visited {
	padding:4px 0;
	display:block;
	text-align:center;
	text-decoration:none;
	background:@main_color;
	color: @menu_text_color;
	width:148px;

}
#menu_class# > ul li:hover a, #menu_class# > ul li a:hover, #menu_class# > ul li a:active {
	padding:4px 0;
	display:block;
	text-align:center;
	text-decoration:none;
	background: lighten(@main_color,15%);
	color:#ffffff;
	width:146px;
	border-left:1px solid #ffffff;
	border-right:1px solid #ffffff;
}
#menu_class# > ul li ul {
	margin:0;
	padding:1px 1px 0;
	list-style:none;
	display:none;
	background:#ffffff;
	width:146px;
	position:absolute;
	top:21px;
	left:-1px;
	border:1px solid @main_color;
	border-top:none;
}
#menu_class# > ul li:hover ul {
	display:block;
}
#menu_class# > ul li ul li {
	width:146px;
	clear:left;
	width:146px;
}
#menu_class# > ul li ul li a:link, #menu_class# > ul li ul li a:visited {
	clear:left;
	background:@main_color;
	padding:4px 0;
	width:146px;
	border:none;
	border-bottom:1px solid #ffffff;
	position:relative;
	z-index:1000;
}
#menu_class# > ul li ul li:hover a, #menu_class# > ul li ul li a:active, #menu_class# > ul li ul li a:hover {
	clear:left;
	background: lighten(@main_color,15%);
	padding:4px 0;
	width:146px;
	border:none;
	border-bottom:1px solid #ffffff;
	position:relative;
	z-index:1000;
}
#menu_class# > ul li ul li ul.navigation-3 {
	display:none;
	margin:0;
	padding:0;
	list-style:none;
	position:absolute;
	left:145px;
	top:-2px;
	padding:1px 1px 0 1px;
	border:1px solid @main_color;
	border-left:1px solid @main_color;
	background:#ffffff;
	z-index:900;
}
#menu_class# > ul li ul li:hover ul.navigation-3 {
	display:block;
}
#menu_class# > ul li ul li ul.navigation-3 li a:link, #menu_class# > ul li ul li ul.navigation-3 li a:visited {
	background:@main_color;
}
