css:
@color: [[main_color:#F93]]; /* #F93 */
@darker: darken(@color, 20%); /* #C60 */

#menu_class# {
	border:none;
	border:0px;
	margin:0px;
	padding:0px;
	font: 67.5% "Lucida Sans Unicode", "Bitstream Vera Sans", "Trebuchet Unicode MS", "Lucida Grande", Verdana, Helvetica, sans-serif;
	font-size: [[menu_font_size:14px]];
	font-weight:bold;
        width: [[menu_width:auto]];
	}
#menu_class#  ul{
	background: @color;
	height: 50px;
	list-style:none;
	margin:0;
	padding:0;
	-webkit-border-radius: 15px;
	-moz-border-radius: 15px;
	border-radius: 15px;
	-webkit-box-shadow: inset 0px 16px 0px 0px rgba(255, 255, 255, .1);
	-moz-box-shadow: inset 0px 16px 0px 0px rgba(255, 255, 255, .1);
	box-shadow: inset 0px 16px 0px 0px rgba(255, 255, 255, .1);
	}
	#menu_class#  li{
		float:left;
		padding:0px 0px 0px 15px; 
		}
	#menu_class#  li a{
		color: [[menu_text_color:#000]];
		display:block;
		font-weight:normal;
		line-height:50px;
		margin:0px;
		padding:0px 25px;
		text-align:center;
		text-decoration:none;
		}
	#menu_class#  li a:hover{
		background: @darker;
		color: [[menu_text_hover_color:#FFFFFF]];
		text-decoration:none;
		-webkit-box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, .3);
		-moz-box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, .3);
		box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, .3);
		}
	#menu_class#  ul li:hover a{
		background: @darker;
		color: [[menu_text_hover_color:#FFFFFF]];
		text-decoration:none;
		}
	#menu_class#  li ul{
		display:none;
		height:auto;
		padding:0px;
		margin:0px;
		border:0px;
		position:absolute;
		width:[[sub_menu_width:200px]];
		z-index:200;
		}
	#menu_class#  li:hover ul{
		display:block;		
		}
	#menu_class#  li li {
		display:block;
		float:none;
		margin:0px;
		padding:0px;
		width: [[sub_menu_width:200px]];
		background: @color;
		/*this is where the rounded corners for the dropdown disappears*/
		}
	#menu_class#  li:hover li a{
		background:none;
		}
	#menu_class#  li ul a{
		display:block;
		height:50px;
		font-size:12px;
		font-style:normal;
		margin:0px;
		padding:0px 10px 0px 15px;
		text-align:left;
		}
	#menu_class#  li ul a:hover, #menu_class#  li ul li:hover a{
		border:0px;
		color:[[menu_text_hover_color:#FFFFFF]];
		text-decoration:none;
		background: @darker;
		-webkit-box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, .3);
		-moz-box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, .3);
		box-shadow: inset 0px 0px 7px 2px rgba(0, 0, 0, .3); 
		}
