css:
@main_color: [[main_color:#00b8fd]];
@menu_background_color: [[menu_background_color:#353535]];


.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}

#menu_class# ul
{
	margin:0;
	padding:0;
	list-style-type:none;
	width: [[menu_width:auto]];
	position:relative;
	display:block;
	font-size: [[menu_font_size:12px]];
	font-weight:bold;
	background: @menu_background_color;
	font-family:Arial, Helvetica, sans-serif;
	border-bottom:1px solid @menu_background_color;
        .clearfix();
}

#menu_class# li
{
	display:block;
	float:left;
	margin:0;
	padding:0 4px;
}

#menu_class# li a
{
	display:block;
	float:left;
	color:[[menu_text_color: #999999]];
	text-decoration:none;
	font-weight:bold;
	padding:10px 20px 7px 20px;
        border-bottom: 3px solid transparent;

}

#menu_class# li a:hover
{	
	color:[[menu_text_hover_color:#FFFFFF]];
	border-bottom: 3px solid @main_color;		
}


#menu_class# li.active a
{
	display:inline;
	border-bottom: 3px solid @main_color;	
	float:left;
	margin:0;
}
