css:
#menu_class# {
       float: left;
	width:100%;
	background:#efefef;
	font-size:93%;
	line-height:normal;
	border-bottom:1px solid #666;
	}

#menu_class# ul {
	margin:0;
	padding:10px 10px 0 50px;
	list-style:none;
	}
#menu_class# li {
	display:inline;
	margin:0;
	padding:0;
	}
#menu_class# a {
	float:left;
	background:url("#include_path#tableftf.gif") no-repeat left top;
	margin:0;
	padding:0 0 0 4px;
	text-decoration:none;
	}
#menu_class# a span {
	float:left;
	display:block;
	background:url("#include_path#tabrightf.gif") no-repeat right top;
	padding:5px 15px 4px 6px;
	color:#666;
	}
	
/* Commented Backslash Hack hides rule from IE5-Mac \*/
#menu_class# a span {float:none;}
/* End IE5-Mac hack */

#menu_class# .active a span,
#menu_class# a:hover span {color:#FFFFFF;}

#menu_class# .active a,
#menu_class# a:hover {background-position: 0 -42px;}

#menu_class# .active a span,
#menu_class# a:hover span {background-position: 100% -42px;}
