/******   H3    */
#menu_class# ul{
	margin:0;
	padding:0;
	list-style-type:none;
	width:[[menu_width:auto]];
	position:relative;
	display:block;
	height:36px;
	text-transform:uppercase;
	font-size:[[menu_font_size:12px]];
	font-weight:bold;
	background:transparent url("#include_path#off.gif") repeat-x top left;
	font-family:Helvetica,Arial,Verdana,sans-serif;
	border-bottom:4px solid #336666;
	border-top:1px solid #C0E2D4;
}
#menu_class# li{
	display:block;
	float:left;
	margin:0;
	pading:0;}
#menu_class# li a{
	display:block;
	float:left;
	color:[[menu_text_color:#874B46]];
	text-decoration:none;
	font-weight:bold;
	padding:12px 20px 0 20px;
	height:24px;
	background:transparent url("#include_path#divider.gif") no-repeat top right;
	}
#menu_class# li a:hover{
	background:transparent url("#include_path#hover.gif") no-repeat top right;	
	color:[[menu_text_hover_color:#874B46]];
	}
