css:
@main_color: [[main_color:#7AB900]];
@menu_background_color: [[menu_background_color: #000]];

.clearfix() {
  zoom: 1;
  &:before { content: ''; display: block; }
  &:after { content: ''; display: table; clear: both; }
}

#cssmenu {
  .clearfix();
width:[[menu_width:auto]];
background: @menu_background_color;
padding: 8px 0;

}
#menu_class# ul {
list-style-type:none;

position:relative;
display:block;
font-size:[[menu_font_size:12px]];

font-family:Verdana,Helvetica,Arial,sans-serif;
margin:0;
padding: 0;
border-bottom: 1px solid @main_color;
.clearfix();
}

#menu_class# li {
display:block;
float:left;
margin:0;
padding:0;
}

#menu_class# li a {
float:left;
color:[[menu_text_color:#A79787]];
text-decoration:none;
height:24px;
padding:9px 15px 0;
font-weight:normal;

}

#menu_class# li:hover,
#menu_class# .active  {
text-decoration:none;
border-bottom: 2px solid @main_color;
}
#menu_class# li:hover a {
color: [[menu_text_hover_color:#fff]];
}

#menu_class# .active a {
color:#fff;
font-weight:700;
}
