css:
#menu_class# {
	width: 200px;
	border-style: solid solid none solid;
	border-color: #D76100;
	border-size: 1px;
	border-width: 1px;
	padding:0px;
	}
	
	#menu_class# ul{
	margin:0px;
	padding:0px;
	list-style:none;
	}
	
	#menu_class# li a {
		height: 32px;
		height: 24px;
		text-decoration: none;
		font-weight:normal;
		color: #9E3C02;
		display: block;
		background: url(#include_path#menu4.gif);
		padding: 8px 0 0 30px;
		}
		
	#menu_class# li a:hover {
		color: #fff;
		background: url(#include_path#menu4.gif) 0 -32px;
		padding: 8px 0 0 30px;
		}
		
	#menu_class# li a:active {
		color: #fff;
		background: url(#include_path#menu4.gif) 0 -64px;
		padding: 8px 0 0 30px;
		}
