css:
#menu_class# {
	width: 200px;
	border-style: solid solid none solid;
	border-color: #94AA74;
	border-size: 1px;
	border-width: 1px;
	}
#menu_class# ul{
	list-style:none;
	padding:0px;
	margin:0px;
	}
	
#menu_class# li a {
	height: 32px;
  	height: 24px;
	text-decoration: none;
	font-weight:normal;
	}	
	
#menu_class# li a:link, #menu_class# li a:visited {
	color: #5E7830;
	display: block;
	background: url(#include_path#menu1.gif);
	background-repeat:no-repeat;
	padding: 8px 0 0 10px;
	}
	
#menu_class# li a:hover {
	color: #26370A;
	background: url(#include_path#menu1.gif) 0 -32px;
	background-repeat:no-repeat;
	padding: 8px 0 0 10px;
	}
	
#menu_class# li a:active {
	color: #26370A;
	background: url(#include_path#menu1.gif) 0 -64px;
	background-repeat:no-repeat;
	padding: 8px 0 0 10px;
	}
