css:
#menu_class# {
	background: #333;
	list-style: none;
	margin: 0;
	padding: 0;
	width: 12em;
}
#menu_class# li {
	font: 67.5% "Lucida Sans Unicode", "Bitstream Vera Sans", "Trebuchet Unicode MS", "Lucida Grande", Verdana, Helvetica, sans-serif;
	margin: 0;
	padding: 0;
       list-style: none;
}
#menu_class# a {
	background: #333;
	border-bottom: 1px solid #393939;
	color: #ccc;
	display: block;
	margin: 0;
	padding: 8px 12px;
	text-decoration: none;
	font-weight:normal;
}
#menu_class# a:hover {
	background: #2580a2 url("#include_path#hover.gif") left center no-repeat;
	color: #fff;
	padding-bottom: 8px;
}
