css:
#menu_class# {
	width: 200px;
	border-style: solid solid none solid;
	border-color: #94AA74;
	border-width: 1px;

	}
#menu_class# ul{
	list-style:none;
	margin:0px;
	padding:0px;
	}

#menu_class# li a {
	height: 32px;
  	height: 24px;
	text-decoration: none;
	font-weight:normal;
        display: block;
	}	

#menu_class# li a:link, #menu_class# li a:visited {
	color: #FFFFFF;
	display: block;
	background: url(#include_path#v6.gif);
	background-repeat:no-repeat;
	padding: 8px 0 0 50px;
	}

#menu_class# li a:hover {
	color: #666666;
	background: url(#include_path#v6.gif) 0 -32px;
	background-repeat:no-repeat;
	padding: 8px 0 0 50px;
	}

#menu_class# li a:active {
	color: #666666;
	background: url(#include_path#v6.gif) 0 -64px;
	background-repeat:no-repeat;
	padding: 8px 0 0 50px;
	}
