css:
@color: [[main_color: #7d7d7d]];
@darker: darken(@color, 10%);
@lighter: lighten(@color, 10%);

#menu_class# ul {margin: 0; padding: 7px 6px 0; background: @color url(#include_path#overlay.png) repeat-x 0 -110px; line-height: 100%; border-radius: 1em; font: normal .8em/1.5em Arial, Helvetica, sans-serif; -webkit-border-radius: 5px; -moz-border-radius: 5px; border-radius: 5px; -webkit-box-shadow: 0 1px 3px rgba(0,0,0, .4); -moz-box-shadow: 0 1px 3px rgba(0,0,0, .4); width: [[menu_width:auto]];}
#menu_class# li {margin: 0 5px; padding: 0 0 8px; float: left; position: relative; list-style: none; }
#menu_class# a,
#menu_class# a:link {font-weight: bold; font-size: [[menu_font_size:13px]]; color: #e7e5e5; text-decoration: none; display: block; padding:  8px 20px; margin: 0; border-radius: 5px; -webkit-border-radius: 5px; -moz-border-radius: 5px;	 text-shadow: 0 1px 1px rgba(0,0,0, .3); }
#menu_class# a:hover {background: #000; color: #fff;}
#menu_class# .active a, 

#menu_class# li:hover > a {
background: @lighter url(#include_path#overlay.png) repeat-x 0 -40px; 
background: #666 url(#include_path#overlay.png) repeat-x 0 -40px; 
color: #444; 
border-top: solid 1px  #f8f8f8; 
-webkit-box-shadow: 0 1px 1px rgba(0,0,0, .2); 
-moz-box-shadow: 0 1px 1px rgba(0,0,0, .2); 
box-shadow: 0 1px 1px rgba(0,0,0, .2); 
text-shadow: 0 1px 0 rgba(255,255,255, 1); }

#menu_class# ul ul li:hover a,
#menu_class# li:hover li a {background: none; border: none; color: #666; -webkit-box-shadow: none; -moz-box-shadow: none;}
#menu_class# ul ul a:hover {background: @color url(#include_path#overlay.png) repeat-x 0 -100px !important; color: #fff !important; -webkit-border-radius: 5px; -moz-border-radius: 5px; border-radius: 5px; text-shadow: 0 1px 1px rgba(0,0,0, .1);}
#menu_class# li:hover > ul {display: block;}
#menu_class# ul ul {z-index: 1000; display: none; margin: 0; padding: 0; width: [[sub_menu_width:185px]]; position: absolute; top: 40px; left: 0; background: #fff url(#include_path#overlay.png) repeat-x 0 0; border: solid 1px #b4b4b4; -webkit-border-radius: 5px; -moz-border-radius: 5px; border-radius: 5px; -webkit-box-shadow: 0 1px 3px rgba(0,0,0, .3); -moz-box-shadow: 0 1px 3px rgba(0,0,0, .3); box-shadow: 0 1px 3px rgba(0,0,0, .3);}
#menu_class# ul ul li {float: none; margin: 0; padding: 3px; }
#menu_class# ul ul a,
#menu_class# ul ul a:link {font-weight: normal;  font-size: [[sub_menu_font_size: 12px]];}
#menu_class# ul:after {content: "."; display: block; clear: both; visibility: hidden; line-height: 0; height: 0;}
* html #menu_class#  ul {height: 1%;}
